INSERT INTO `tintuc` (`id`, `tieuDe`, `hinhAnh`, `noiDung`, `time`, `author`, `idTL`, `tinnoibat`) VALUES
(1, 'Người Sài Gòn trải nghiệm mô hình xe buýt sách', 'xe-buyt-sach-1-1524287651_1200x0.jpg', 'Xe buýt dài 6 m, rộng 1,5 m chứa hàng trăm cuốn sách vừa ra mắt sáng nay tại đường sách TP HCM.\r\nNgười Sài Gòn trải nghiệm mô hình xe buýt sách\r\nSáng 21/4, mô hình xe buýt sách ra mắt tại Đường sách Nguyễn Văn Bình (quận 1, TP HCM), thu hút đông đảo người dân và du khách tham quan.\r\n\r\nSự kiện do Sở Giao thông Vận tải phối hợp với Công ty TNHH Đường sách TP HCM tổ chức, giới thiệu bộ ba sản phẩm: xe buýt sách - mô hình thư viện thu nhỏ, trạm chờ xe buýt - mô phỏng trạm chờ hiện đại, trang bị kệ sách tại chỗ và bảng tra cứu thông tin điện tử.\r\n\r\nNgười Sài Gòn trải nghiệm mô hình xe buýt sách\r\nChiếc xe dài 6 m, rộng 1,5 m, mô phỏng từ chuyến xe buýt số 8 - chuyến xe gắn liền với sinh viên, xuất phát từ Bến xe quận 8 đến Làng đại học Thủ Đức.\r\n\r\nBên trong xe buýt như một thư viện thu nhỏ, gồm các kệ trưng bày nhiều loại sách, báo phục vụ nhu cầu tìm kiếm thông tin của du khách.\r\n\r\nNgười Sài Gòn trải nghiệm mô hình xe buýt sách\r\n\"Mô hình xe buýt rất thú vị. Tôi rất mong thành phố sẽ có thêm nhiều mô hình như này tại các nơi công cộng\", chị Hiệp (quận Phú Nhuận) đưa con nhỏ tham quan đường sách, chia sẻ.\r\n\r\nNgười Sài Gòn trải nghiệm mô hình xe buýt sách\r\nChị Julia Klyuchnikova (người Nga) đưa con trai trải nghiệm mô hình xe buýt sách. \"Ý tưởng xe buýt sách rất độc đáo. Tôi rất thích\", chị nói.\r\n\r\nNgười Sài Gòn trải nghiệm mô hình xe buýt sách\r\nBên trong mô hình buýt sách có các bảng thông tin giống hệt xe buýt thật.\r\n\r\nNgười Sài Gòn trải nghiệm mô hình xe buýt sách\r\n\"Tôi rất thích mô hình này vì nó tạo cảm giác thân quen cho chúng tôi, những người thường đi xe buýt hàng ngày đến trường\", Nguyễn Thị Hương, sinh viên năm hai Đại học Khoa học Xã hội và Nhân văn TP HCM, nói.\r\n\r\nNgười Sài Gòn trải nghiệm mô hình xe buýt sách\r\nNhiều cuốn sách kinh điển với nhiều chủ đề như Văn học, tiểu thuyết, lịch sử... được trưng bày trong xe buýt.\r\n\r\nNgười Sài Gòn trải nghiệm mô hình xe buýt sách\r\nXe buýt sử dụng hai ngôn ngữ là Tiếng Việt, Tiếng Anh để phục vụ nhu cầu đọc của người dân và khách tham quan.\r\n\r\nNgười Sài Gòn trải nghiệm mô hình xe buýt sách\r\nBạn trẻ thích thú chụp hình trong xe. \"Xe buýt sách đã tạo lên một diện mạo mới đầy năng động, thân thiện và thiết thực với người yêu sách\", Cẩm Chi, sinh viên Đại học Luật TP HCM, chia sẻ.\r\n\r\nNgười Sài Gòn trải nghiệm mô hình xe buýt sách\r\nCạnh xe buýt là mô hình nhà chờ với các kệ sách, băng ghế gỗ, mái che được thiết kế theo hình trang sách để người dân, du khách dễ dàng nghỉ ngơi.\r\n\r\nNgười Sài Gòn trải nghiệm mô hình xe buýt sách\r\nKhu vực đầu đường sách còn có bảng tra cứu thông tin điện tử có link với trang web và fanpage của đường sách giúp người yêu sách theo dõi lịch tổ chức các sự kiện tại đây và thông tin cụ thể quyển sách nào được bán.\r\n\r\nÔng Hà Lê Ân, Phó giám đốc Trung tâm Quản lý và Điều hành vận tải hành khách công cộng (Sở GTVT) cho biết việc ra mắt xe buýt sách nhằm tạo một không gian đọc mới mẻ, sáng tạo tại đường sách vừa dùng hình ảnh xe buýt để tuyên truyền ý thức sử dụng phương tiện giao thông công cộng, một trong những giải pháp tránh ùn tắc giao thông cho thành phố hiện nay.\r\n\r\n\"Trong năm nay, chúng tôi sẽ tiếp tục thí điểm ba mô hình dọc Xa lộ Hà Nội gồm, nhà chờ xe buýt, nhà vệ sinh và bảng thông tin tra cứu sách và xe buýt để phục vụ tốt hơn nhu cầu đi lại, đọc sách người dân và du khách. Kinh phí thực hiện bằng nguồn vốn xã hội hóa\", ông Ân nói.', 'Thứ bảy, 21/4/2018 | 13:42 GMT+7', 'Duy Mạnh', 1, 1),
(2, 'Thanh Hóa yêu cầu xã trả lại phí đồng cỏ chăn nuôi trâu bò cho dân', '20180417-100435-3781-1524270911.jpg', 'Khẳng định việc hợp tác xã thu phí đồng cỏ trên đàn trâu bò là không đúng, UBND TP Thanh Hóa buộc trả lại cho nông dân trước 30/4.\r\nNhiều nông dân Thanh Hóa phải đóng phí đồng cỏ khi chăn thả trâu bò\r\n\r\nChiều 20/4, sau cuộc làm việc của đoàn liên ngành tại xã Thiệu Dương, UBND thành phố Thanh Hóa ra kết luận về một số khoản thu của Hợp tác xã dịch vụ Minh Anh.\r\n\r\nTheo đó, Thanh Hoá xác định việc Hợp tác xã này thu tiền phí chăn thả trâu, bò của nông dân như báo chí phản ánh là có thật.\r\n\r\nKhoản này do Hợp tác xã thu trên cơ sở họp thống nhất với 13/15 hộ chăn thả trâu bò, số lượng 100 con, nhằm thế chấp cho việc đảm bảo không để gia súc phá hoại hoa màu, tài sản của nhân dân.\r\n\r\nNông dân ở xã Thiệu Dương muốn thả trâu bò ra đồng phải đóng phí đồng cỏ thế chấp. Ảnh: Lam Sơn.\r\nNông dân ở xã Thiệu Dương muốn thả trâu bò ra đồng phải đóng phí đồng cỏ thế chấp. Ảnh: Lam Sơn.\r\n\r\nHợp tác xã sẽ trả lại tiền cho các hộ trong trường hợp người dân không còn nuôi hoặc không gây thiệt hại hoa màu, tài sản trong quá trình chăn thả trâu bò; việc trả lại tiền thế chấp được thực hiện sau mỗi vụ sản xuất.\r\n\r\nTrường hợp các hộ chăn thả gia súc gây thiệt hại, hợp tác xã sẽ mời các bên liên quan dùng số tiền thế chấp bồi thường cho gia đình có hoa màu bị hư hại. Thành phố khẳng định, khoản thế chấp này không đúng quy định của pháp luật nên chỉ đạo Hợp tác xã Minh Anh trả lại cho các hộ dân trước ngày 30/4.\r\n\r\nVề khoản 5 triệu đồng tiền thế chấp máy gặt, máy lồng, báo cáo của xã cho rằng đây là khoản góp vốn của các thành viên hợp tác xã theo điều lệ hoạt động, hộ gia đình nào muốn trở thành thành viên của hợp tác xã phải đóng góp theo quy định.\r\n\r\nNgoài ra, Tổ hợp tác cơ giới hóa trong sản xuất lúa ở xã Thiệu Dương thu (20 thành viên) còn thu 10.000 đồng mỗi đầu sào của các hộ có máy sản xuất nông nghiệp, nhằm trang trải cho hoạt động điều hành, hỗ trợ thăm hỏi, ốm đau của các thành viên trong tổ. Thành phố yêu cầu Tổ dịch vụ không được thu của các thành viên nếu họ không tự nguyện đóng góp.\r\n\r\nUBND thành phố Thanh Hóa cũng yêu cầu xã Thiệu Dương và các xã, phường khác rà soát, báo cáo về việc thu phí, lệ phí, nghiêm cấm thu các khoản đóng góp trái quy định.\r\n\r\nGần đây, nhiều nông dân xã Thiệu Dương (TP Thanh Hoá) bức xúc phản ánh, họ bị Hợp tác xã dịch vụ Minh Anh ép phải đóng phí chăn thả trâu, bò nếu muốn cho gia súc ra đồng ăn cỏ.\r\n\r\nCác khoản phí mà Hợp tác xã Minh Anh đặt ra gồm phí đồng cỏ 100.000 đồng mỗi con một năm. Ngoài ra, các hộ còn phải đóng phí thế chấp tiền chăn thả gia súc, gia cầm thu theo các mức độ khác nhau. Cụ thể, hộ có 1 - 3 con trâu, bò thu 300.000 đồng; 3 - 5 con, mức thu là 500.000 đồng; 5 - 10 con thu một triệu đồng và hộ từ 10 con trở lên thu hai triệu đồng. Nếu hộ nào không nộp, người của hợp tác xã sẽ không cho chăn thả trâu, bò trên đồng.\r\n\r\nTrong Quy ước bảo vệ đồn điền, Hợp tác xã Minh Anh còn đề ra các điều khoản cấm người dân chăn dắt, thả trâu, bò, dê, vịt, gà, ngan, ngỗng, lợn… trên tất cả các xứ đồng, kể cả bờ mương, bờ kênh do Hợp tác xã quản lý; các chủ máy gặt, máy lồng đất trước khi xuống đồng phải đến đăng ký với hợp tác xã, để có kế hoạch phân bổ diện tích cho từng chủ máy.\r\n\r\nTất cả các trường hợp vi phạm, dù vô tình hay cố ý, đều lập biên bản và xử phạt 100.000 - 300.000 đồng (lần thứ nhất); mức xử phạt với trâu, bò, dê… mỗi con từ 100.000 - 200.000 đồng/lần, nếu tái phạm sẽ xử phạt từ 200.000 - 300.000 đồng. Vịt, gà, ngan, ngỗng thả ra đồng sẽ bị xử phạt từ 50.000 - 100.000 đồng/lần, tái phạm sẽ xử phạt từ 100.000 - 200.000 đồng…\r\n\r\nÔng Dương Đình Minh, Giám đốc Hợp tác xã dịch vụ Minh Anh, thừa nhận đã thu những khoản tiền nói trên, song lý giải, việc thu các khoản phí đều \"trên tinh thần tự nguyện” và dựa trên Quy ước đồn điền của làng từ xưa.', 'Thứ bảy, 21/4/2018 | 09:00 GMT+7', 'Quốc Vinh', 1, 0),
(3, 'Phó bí thư Đồng Nai vượt quyền, \'ưu ái\' cho công ty của chồng', 'IMG-8937-4950-1524206668.jpg', 'Thanh tra Chính phủ vừa kết luận thanh tra toàn diện dự án Khu dân cư thương mại dịch vụ xã Phước Tân, TP Biên Hòa (Đồng Nai) do công ty TNHH Cường Hưng làm chủ đầu tư. Đây là công ty do ông Đỗ Tịnh - chồng bà Phan Thị Mỹ Thanh - Phó bí thư Tỉnh ủy Đồng Nai làm giám đốc.\r\n\r\nBến thủy nội địa công ty Cường Hưng được bà Thanh cấp phép trái với chức năng nhiệm vụ. Ảnh: Phước Tuấn\r\nBến thủy nội địa của Công ty Cường Hưng được bà Thanh cấp phép trái với chức năng nhiệm vụ. Ảnh: Phước Tuấn.\r\n\r\nDự án có diện tích 83 hecta, sau đó điều chỉnh tăng lên gần 92 hecta, được UBND tỉnh Đồng Nai chấp thuận chủ trương vào năm 2012. Hiện dự án mới triển khai san lấp trong khi chưa hoàn thành về tiến độ đầu tư và thủ tục pháp lý.\r\n\r\nQua thanh tra, xác định Công ty Cường Hưng tạo dựng hồ sơ để xin cấp giấy chứng nhận đăng ký doanh nghiệp không đúng quy định pháp luật, không thực hiện thuê kiểm toán độc lập, hệ thống sổ sách kế toán không thực hiện đúng quy định, hoạt động của công ty không đúng theo luật doanh nghiệp và điều lệ công ty. Về việc giả mạo chữ ký, ông Tịnh thừa nhận nhân viên tự ký cho thành viên góp vốn để hợp thức hóa hồ sơ trình lên sở Kế hoạch và Đầu tư.\r\n\r\nNgoài ra, công ty này còn tổ chức hoạt động xây dựng khi chưa có giấy phép, chưa được phê duyệt thiết kế cơ sở tại dự án là trái quy định pháp luật; việc tranh chấp vốn giữa các thành viên kéo dài, chưa minh bạch sổ sách kế toán, chứng từ làm ngưng trệ hoạt động công ty, chậm tiến độ dự án...\r\n\r\nKết luận của thanh tra khẳng định UBND tỉnh Đồng Nai, các sở ngành liên quan có biểu hiện buông lỏng quản lý dẫn đến các sai phạm từ quá trình chuẩn bị đến đầu tư dự án.\r\n\r\nCông ty của chồng Phó bí thư được \"chống lưng\"\r\n\r\nThanh tra Chính phủ nêu rõ, bà Thanh thời kỳ làm Phó chủ tịch UBND tỉnh Đồng Nai đã ký quyết định cho phép đầu tư dự án chưa đúng quy định pháp luật; ký văn bản báo cáo xin ý kiến Bộ Xây dựng chưa đúng sự thật, chưa đúng tiến độ thực hiện dự án; ký văn bản cho phép Công ty Cường Hưng được tính chi phí xây lắp tuyến đường vào chi phí hợp lý của dự án khu dân cư là chưa đúng với chủ trương chung của tỉnh.', 'Thứ bảy, 21/4/2018 | 06:10 GMT+7', 'Thúy Hạnh', 1, 0),
(4, 'Những mập mờ trong vụ liên kết đào tạo với GWIS', 'webtruong-3316-1524309444.jpg', 'Trường liên kết nói việc thẩm định tính pháp lý của GWIS là Bộ Giáo dục, nhưng Bộ nói chỉ \"đồng ý về nguyên tắc\". \r\nNghi vấn nhiều trường Việt Nam liên kết với \'trường ma\' ở Mỹ\r\n\r\nTrường George Washington International School (GWIS) liên kết dạy song bằng ở 14 tỉnh thành Việt Nam, được Đại sứ quán Mỹ thông báo \"không thể tìm thấy\" trên bất kỳ danh sách nào của Cục An ninh nội địa Mỹ, của bang Florida hay California - hai địa phương trường ghi đặt trụ sở. \r\n\r\nXung quanh câu chuyện GWIS liên kết đào tạo tại 14 tỉnh thành Việt Nam, có  nhiều câu hỏi chưa được giải đáp rõ.\r\n\r\nÍt học sinh đủ tín chỉ để được cấp bằng tốt nghiệp THPT của GWIS (Mỹ)  \r\n\r\nTrong cam kết của nhà trường đăng trên website, Tiểu học - THCS - THPT Newton (Hà Nội) - đơn vị đầu tiên liên kết với GWIS - cho biết học sinh sẽ được cấp bằng tốt nghiệp THPT của GW (Mỹ), khi đủ 24 tín chỉ trong chương trình liên kết với GWIS (viết tắt là GW). \r\n\r\nNhững hứa hẹn về quyền lợi của học sinh khi học chương trình liên kết với GWIS đăng trên website của trường Tiểu học - THCS - THPT Newton (Hà Nội), nay đã bị xóa. Ảnh chụp màn hình.\r\nNhững hứa hẹn về quyền lợi của học sinh khi học chương trình liên kết với GWIS đăng trên website của trường Tiểu học - THCS - THPT Newton (Hà Nội), nay đã bị xóa. Ảnh chụp màn hình.\r\n\r\nPhần giới thiệu về chương trình liên kết với GWIS của trường Victory (Đăk Lăk) cũng viết, học sinh được nhận bảng điểm và giấy chứng nhận hoàn thành lớp học, tương đương học bạ từng lớp của Việt Nam, khi kết thúc chương trình mỗi lớp học. Kết thúc lớp 12, học sinh được nhận bằng Tú tài của Mỹ.\r\n\r\nCam kết trên của các trường liên kết đã thuyết phục được nhiều phụ huynh - những người mong muốn con em có song bằng tốt nghiệp THPT của cả Việt Nam và Mỹ. Tuy nhiên, thực tế từ năm 2012 đến nay, trường Newton mới có 6 học sinh nhận được song bằng trong đợt tốt nghiệp năm học 2015-2016. \r\n\r\nGiải thích lý do số học sinh theo học chương trình liên kết nhiều (hiện khoảng 500), nhưng có quá ít người được nhận song bằng tốt nghiệp, Hiệu phó Đặng Huyền Phong cho biết, \"để đạt đủ 24 tín chỉ, học sinh phải học ít nhất từ lớp 9 chương trình GW\". Các em vào lớp 10 mới học chương trình này không thể lấy đủ số tín chỉ, đồng nghĩa với việc không được cấp bằng.\r\n\r\nNhư vậy giữa cam kết đào tạo với thực tế cấp bằng tốt nghiệp THPT của Mỹ là khoảng cách quá xa.\r\n\r\nBằng tốt nghiệp THPT của Mỹ có giá trị thế nào với các đại học Mỹ?\r\n\r\nCũng trong cam kết, trường Newton khẳng định \"chuyển tiếp vào các trường đại học của Mỹ, Anh, Úc... tất cả học sinh có bằng tốt nghiệp GW; chuyển tiếp sang trường nước ngoài tất cả học sinh hệ quốc tế GW từ năm 2\".\r\n\r\nTrường Newton còn thông tin, học sinh học hệ GW sau khi tốt nghiệp THPT sẽ được hưởng học phí ưu đãi như công dân người Mỹ (học bổng đối tác nước ngoài là khoảng 50% học phí) cho 4 năm học tại một số đại học (có nêu tên) ở quốc gia này.\r\n\r\nWebsite của GWIS thiếu mọi thông tin căn bản, như: hiệu trưởng, ban điều hành, ban giáo viên, phòng giáo vụ... \r\nWebsite của GWIS thiếu mọi thông tin căn bản, như: hiệu trưởng, ban điều hành, ban giáo viên, phòng giáo vụ... \r\n\r\nTuy nhiên, Chủ tịch Viện Văn hóa và Giáo dục Việt Nam tại Mỹ, ông Trần Thắng khẳng định \"xã hội Mỹ xem văn bằng tốt nghiệp THPT là kỷ niệm thời trung học\". Nó có tính tượng trưng, không có giá trị trong hồ sơ đại học hay xin việc làm. Ngoài ra, đại học Mỹ chấp nhận văn bằng tốt nghiệp THPT của các quốc gia. Kể cả khi trường Việt Nam không liên kết với GWIS, bằng tốt nghiệp phổ thông vẫn được đại học Mỹ chấp nhận.\r\n\r\nNguyễn Vy - Việt kiều ở bang Arizona (Mỹ) từng học chương trình phổ thông và đại học của quốc gia này cũng cho biết, bằng tốt nghiệp THPT \"chỉ phát cho vui\" chứ không có giá trị khi xét tuyển vào đại học ở Mỹ. \"Các trường chủ yếu dựa vào điểm thi chuẩn hóa SAT hoặc điểm GPA để tuyển sinh viên\", Vy nói.\r\n\r\nDù học sinh mất thời gian, tiền bạc học để đạt 24 tín chỉ, đủ để được cấp bằng tốt nghiệp THPT của GWIS (Mỹ), nhưng văn bằng ấy chẳng đem lại giá trị gì. \r\n\r\nHọc phí chương trình GW giữa các tỉnh chênh nhau hàng chục triệu đồng \r\n\r\nĐại diện các trường học có liên kết với GWIS tại Hà Nội, Đăk Lăk, Đồng Tháp, Phú Yên đều cho biết được đối tác Mỹ cung cấp chương trình, sách giáo khoa và giáo viên nước ngoài dạy các môn Toán, Khoa học, tiếng Anh, Văn học Mỹ. Cùng một chương trình, học phí các trường lại chênh lệch hàng chục triệu đồng. \r\n\r\nĐơn cử, học phí chương trình GW lớp 6-8 của trường Newton là 11,9 triệu đồng/tháng. Trong khi đó, học phí ở trường Victory (Đăk Lăk), cùng hệ GW, cùng khối lớp, là 1 triệu đồng/tháng (với học sinh trong trường) và 2,6 triệu đồng/tháng (với học sinh ngoài trường), tức chỉ tương đương với học phí trường tư thục trên cùng địa bàn.\r\n\r\nVictory còn quảng cáo việc du học tại chỗ qua chương trình liên kết với GWIS \"giúp phụ huynh tiết kiệm chi phí gấp 20 lần so với chương trình này tại Mỹ\".', 'Thứ bảy, 21/4/2018 | 18:17 GMT+7', 'Diễm My', 1, 0),
(5, 'Jackpot “nổ không phanh“, 3 tỷ phú Vietlott xuất hiện trong 1 tuần', '1524320456-207-jackpot-no-khong-phanh-3-ty-phu-vietlott-xuat-hien-trong-1-tuan-jackpot-1524320204-width660height505.jpg', 'Cả 3 kỳ quay số Power 6/55 trong tuần này đều tìm ra chủ nhân của các jackpot 2 tiền tỉ.\r\nJackpot “nổ không phanh“, 3 tỷ phú Vietlott xuất hiện trong 1 tuần - 1\r\n\r\nKết quả kỳ quay 113 của Power 6/55\r\n\r\nCụ thể, sau khi Vietlott tìm được chủ nhân các jackpot 2 của sản phẩm Power 6/55 ở các kỳ quay 111 (mở thưởng hôm 17/4) và 112 (mở thưởng ngày 19/4), thì ngay kỳ quay 113 (mở thưởng tối 21/4) tiếp sau đó đã thêm một jackpot 2 có chủ. Tại kỳ quay 113 này, giải jackpot 2 có trị giá hơn 7,7 tỉ đồng trước thuế.\r\n\r\nNhư vậy, chỉ trong một tuần, jackpot 2 của Power 6/55 đã “nổ” liên tiếp ở 3 kỳ quay. Trong lịch sử Vietlott cũng chưa bao giờ xuất hiện tình huống giải đặc biệt của một sản phẩm liên tục tìm ra chủ nhân như thế này.\r\n\r\nTrái ngược so với jackpot 2, giải jackpot 1 của Power 6/55 vẫn tiếp tục vô chủ dù giá trị đã tăng lên hơn 300 tỉ đồng. Theo quy định, khi jackpot 1 vượt 300 tỉ đồng mà vô chủ, trong khi jackpot 2 có chủ thì jackpot 1 sẽ trở lại mức 300 tỉ đồng. Lý do là phần vượt 300 tỉ đồng của jackpot 1 đã được cộng dồn cho người trúng jackpot 2.', 'Thứ Bảy, 21/04/2018 21:20 PM (GMT+7)', 'Quỳnh Hói', 1, 1),
(6, 'Man Utd đá bán kết Cup FA, Barca tranh Cup Nhà Vua cuối tuần này', 'P180414-054-Liverpool-Bournemo-6815-4913-1524251273.jpg', 'Thông tin lực lượng: Liverpool không có Joel Matip, Adam Lallana và Emre Can. Nathaniel Clyne, Dejan Lovren và Joe Gomez chưa chắc kịp bình phục chấn thương để ra sân. Klopp nhiều khả năng sẽ cho các trụ cột nghỉ ngơi để chuẩn bị cho trận bán kết Champions League lượt đi với AS Roma tuần sau. West Brom không có James Morrison do chấn thương. Daniel Sturridge không thể đá do được mượn từ Liverpool. Jonny Evans, Nacer Chadli, Gareth Barry chưa chắc có thể thi đấu.\r\n\r\nArsenal - West Ham (19h30 Chủ Nhật, 22/4)\r\n\r\nTrận đấu này diễn ra một ngày sau khi Arsenal thông báo chia tay Arsene Wenger vào cuối mùa. Do vậy, các cầu thủ sẽ rất muốn thắng trận này, đồng thời đạt kết quả tốt trong giai đoạn còn lại của mùa giải để làm quà cho người đã 22 năm dẫn dắt \"Pháo thủ\". Với việc chỉ hơn đội thứ bảy Burnley hai điểm, Arsenal đang đứng trước áp lực lớn. Nếu họ bị vượt mặt và Southampton không vô địch Cup FA, Arsenal sẽ phải đá vòng loại thứ hai của Europa League mùa sau. Điều đó có nghĩa là mùa giải của họ có nguy cơ bắt đầu từ ngày 26/7, và lịch du đấu hè của họ sẽ bị ảnh hưởng. Wenger chắc chắn không muốn điều đó xảy ra trong mùa giải cuối của  ông tại vị.\r\n\r\n\r\nThông tin lực lượng: Arsenal không có Santi Cazorla, Henrikh Mkhitaryan trong khi Aaron Ramsey và Jack Wilshere không có thể lực tốt nhất. Wenger nhiều khả sẽ xoay tua để chuẩn bị cho trận bán kết Europa League lượt đi vào tuần sau. West Ham sẽ không có Pedro Obiang, Winston Reid, Sam Byram và Michail Antonio. James Collins chưa chắc có thể thi đấu.\r\n\r\nMan City - Swansea (22h30 Chủ Nhật, 22/4)\r\n\r\nChức vô địch đã nằm trong tay Man City, và thầy trò Pep Guardiola sẽ \"dạo chơi\" ở phần còn lại của mùa giải với mục tiêu phá thêm một số kỷ lục. Họ cần thêm ba trận thắng để phá kỷ lục 30 chiến thắng của Chelsea ở mùa 2016-2017. Man City cũng cần thêm chín điểm để phá kỷ lục 95 điểm do Chelsea lập ở mùa 2004-2005. Họ cần ghi thêm 11 bàn để phá kỷ lục số bàn trong một mùa, cũng do Chelsea nắm giữ ở mùa 2009-2010 với 103 bàn. Với việc còn năm trận chưa đá, 15 điểm đang chờ nắm lấy, những kỷ lục này đều trong tầm có thể chạm tới với Man City.\r\n\r\n\r\nThông tin lực lượng: Man City không có Fernandinho ở trận này do thẻ phạt. Sergio Aguero nghỉ đến cuối mùa do chấn thương. John Stones và Benjamin Mendy vẫn chưa có thể lực tốt nhất. Swansea thiếu Wilfried Bony, Leroy Fer và Renato Sanches. Angel Rangel, Luciano Narsingh, Mike van der Hoorn và Sam Clucas cần phải đợi kiểm tra y tế.\r\n\r\nMan Utd - Tottenham (23h15 thứ Bảy, 21/4)\r\n\r\nMauricio Pochettino sẽ không thể được xem là thành công nếu chưa giúp Tottenham đoạt một danh hiệu. \"Gà trống\" là một đội bóng bùng nổ tại Ngoại hạng Anh nhưng luôn thi đấu tồi ở các giải Cup trong những năm gần đây. Vào tới bán kết Cup FA lần này, Pochettino hy vọng sẽ giúp đội nhà giải cơn khát danh hiệu. Tottenham sẽ có lợi thế lớn khi được đá ở sân Wembley, vốn là sân nhà của họ mùa hiện tại. Thầy trò Pochettino đã vượt qua Man Utd trên sân đấu này ở Ngoại hạng Anh năm nay. Tuy nhiên, Cup FA là hy vọng cuối cùng của \"Quỷ Đỏ\", và Jose Mourinho sẽ không dễ để đội bóng ông dẫn dắt trắng tay. \r\n\r\n\r\nThông tin lực lượng: Man Utd có lực lượng mạnh nhất, chỉ thiếu thủ môn dự bị Sergio Romero. Tottenham sẽ không có Kyle Walker-Peters, Harry Winks và hậu vệ trái Danny Rose.\r\n\r\nChelsea - Southampton (22h Chủ Nhật, 22/4)\r\n\r\nAntonio Conte bị đồn là sẽ rời Chelsea trong hè này. Nếu đó là sự thật, với hy vọng vào top 4 Ngoại hạng Anh ngày càng xa vời, HLV người Italy sẽ dồn hết sức cho Cup FA. Conte chắc chắn muốn xua đi nỗi buồn thua Arsenal 1-2 ở trận chung kết năm ngoái. Cup FA là một danh hiệu danh giá, nhưng với Southampton, một đội bóng nhỏ bé so với ba CLB còn lại ở bán kết năm nay, nó còn sẽ mang những ý nghĩa to lớn hơn. Từng được xem là đội có suất ổn định ở giữa bảng Ngoại hạng Anh, Southampton đang đứng trước nguy cơ rớt hạng. Chỉ sáu ngày sau trận này, họ sẽ phải đá trận cầu sáu điểm trên sân đối thủ trong cuộc chiến trụ hạng - Swansea.\r\n\r\n\r\nThông tin lực lượng: Chelsea không có Marcos Alonso do án treo giò. Ethan Ampadu và David Luiz không thể ra sân do chấn thương.  Southampton cũng có trung vệ Jack Stephens phải ngồi ngoài vì thẻ phạt. Mario Lemina dính chấn thương trong khi Sofiane Boufal chưa chắc có thể ra sân.\r\n\r\nBarca - Sevilla (2h30 Chủ Nhật, 22/4)\r\n\r\nSau trận thua sốc AS Roma tại Champions League, Barca đã lấy lại sự tự tin ở sân chơi trong nước bằng các trận thắng Valencia rồi hòa Celta Vigo hồi giữa tuần với một đội hình xoay tua. Điều đó cho thấy HLV Ernesto Valverde đã chuẩn bị kỹ cho trận chung kết Cup Nhà Vua với Sevilla, quyết tâm mang về danh hiệu đầu tiên cho đội nhà trước ngày đăng quang La Liga. Sevilla thì ngược lại. Sau khi loại Man Utd khỏi Champions League, họ trải qua một chuỗi trận chật vật. Việc để đội trong nhóm cuối bảng La Liga Deportivo cầm hòa hồi giữa tuần khiến mạch không thắng của thầy trò Vincenzo Montella kéo dài thành bảy trận.', 'Thứ bảy, 21/4/2018 | 08:03 GMT+7', 'Chánh Doremon', 2, 0),
(7, 'Nguyễn Tấn Hoài thắng chặng thứ liên tiếp ở Cup Truyền hình', 'bai-5317-1524303436.jpg', 'Tay đua của Đồng Tháp tiếp tục về nhất ở chặng 22 giải đua xe đạp xuyên Việt tranh Cup Truyền hình TP HCM 2018.\r\nÁo Xanh Cup Truyền hình gặp nạn gần đích đến / Nguyễn Thành Tâm lần thứ sáu thắng chặng ở Cup Truyền hình 2018\r\n\r\nChặng đua sáng nay 21/4 từ Bảo Lộc về Bình Dương dài 156 km tái hiện kịch bản của các chặng trước. Theo đó, ê-kip An Giang đang giữ Áo Vàng, và tập hợp lực lượng để kéo đoàn đua đi đông về đủ.\r\n\r\nTrên đường đua, các đội khác liên tục tìm cách tấn công để đánh chiếm thứ bậc, nhưng không thành công. Về gần cuối chặng, An Giang điều hai ngoại binh -  Ali Khademi và Jaier - bứt ra khỏi tốp đông để tạo một nhóm nhỏ đi đầu. Nhóm này sau đó gồm các tay đua Nguyễn Tấn Hoài (Đồng Tháp), Trần Thanh Điền (VUS TP HCM), Nito (Ynghua Đồng Nai), Trần Lê Minh Tuấn (Quân khu 7) bứt đi thành công để cùng nhau về đích.\r\n\r\nTại đích đến trước sân vận động Gò Đậu (Bình Dương), Nguyễn Tấn Hoài rút thắng Trần Thanh Điền để đoạt ngôi thắng chặng. Thời gian đua của người về đầu chặng 22 này là 3 giờ 30 phút 27 giây, đạt tốc độ trung bình 44,476 km/h.\r\n\r\nTấn Hoài lần thứ hai liên tiếp đoạt ngôi thắng chặng Cup 2018. Ảnh: Văn Thuận.\r\nTấn Hoài lần thứ hai liên tiếp đoạt ngôi thắng chặng Cup 2018. Ảnh: Văn Thuận.\r\n\r\nVề đích trong nhóm đầu, tay đua Ali Khademi đã vươn lên chiếm vị trí thứ hai trên bảng thành tích cá nhân, đẩy Huỳnh Thanh Tùng xuống thứ ba.\r\n\r\nCác danh hiệu cao quý nhất giải vẫn chưa đổi khi Nguyễn Thành Tâm giữ Áo Vàng, Lê Nguyệt Minh mặc Áo Xanh. Trong khi đó, Áo Trắng và Áo Đỏ lần lượt thuộc về Ngô Văn Phương (An Giang), rồi Loic Desriac (Đồng Nai). Đội TP HCM vẫn đứng đầu thành tích đồng đội tổng.\r\n\r\nNgày mai, cuộc đua xe đạp xuyên Việt tranh Cup Truyền hình TP HCM đua chặng 23, từ Bình Dương đi Đồng Tháp, dài 190 kilomet.', 'Thứ bảy, 21/4/2018 | 17:25 GMT+7', 'Thị Thơm', 2, 0),
(8, 'Quan hệ Mourinho - Pogba bị so sánh với Ferguson - Beckham', 'Untitled-1-3055-1524214275.jpg', 'Tờ Sportsmail cho rằng tiền vệ người Pháp đang \"đi theo vết xe đổ\" của đàn anh trong vụ bê bối cách đây nhiều năm.\r\nMourinho và Scholes khen Pogba sau trận thắng Bournemouth / 10 lần Mourinho chỉ trích cầu thủ mùa này\r\n\r\nJose Mourinho hiếm khi nói chuyện với Paul Pogba sau trận thua West Brom 0-1. Dù được đá trận gặp Bournemouth, tuyển thủ Pháp tiếp tục bị thay ra. Và bằng chứng về sự lạnh nhạt giữa hai thầy trò tại Man Utd thể hiện qua khuôn mặt lạnh lùng của Mourinho khi ra lệnh cho Pogba trở lại ghế dự bị, sau khi cựu cầu thủ Juventus định đi vào đường hầm.\r\n\r\nMối quan hệ giữa Mourinho và Pogba đang ngày càng xấu đi. Ảnh: AP.\r\nMối quan hệ giữa Mourinho và Pogba đang ngày càng xấu đi. Ảnh: AP.\r\n\r\nNguồn tin bên trong sân Old Trafford của Sportsmail cho biết có nhiều điểm tương đồng giữa vụ việc của Mourinho - Pogba và vụ xích mích nổi tiếng giữa cựu HLV Alex Ferguson và David Beckham hồi năm 2003. Khi được hỏi liệu Pogba có thể rời Man Utd hè này, cựu tiền vệ Paul Scholes nói: \"Tôi nghĩ cậu ấy có thể. Từ bên ngoài nhìn vào, mối quan hệ giữa hai người có vẻ không tốt\". Scholes từng chứng kiến mâu thuẫn giữa Ferguson và Beckham, một cuộc chiến giữa hai cá tính mạnh mẽ khác trong phòng thay đồ mà kết thúc là sự ra đi của cựu thủ quân tuyển Anh.\r\n\r\nMâu thuẫn giữa Ferguson và Beckham kéo dài nhiều năm, sau khi cựu cầu thủ Man Utd kết hôn với ca sĩ Victoria Adams. Ferguson khi đó lo ngại Beckham bị ảnh hưởng bởi lối sống ngôi sao và xao nhãng chuyên môn. \r\n\r\nTrong trường hợp của Pogba, tiền vệ người Pháp có một khao khát gây chú ý, thường xuyên làm những trò gây náo nhiệt, thích đổi kiểu tóc liên tục, thích nhảy nhót và hoạt động trên mạng xã hội. Một số ý kiến cho rằng Pogba xem trọng sự nổi tiếng của bản thân vượt lên trên đội bóng.\r\n\r\nBeckham và Ferguson từng xảy ra xích mích lớn dẫn đến sự ra đi của cựu tiền vệ người Anh. Ảnh: Reuters.\r\nBeckham và Ferguson từng xảy ra xích mích lớn dẫn đến sự ra đi của cựu tiền vệ người Anh. Ảnh: Reuters.\r\n\r\n\"Pogba hành động giống như một đứa trẻ khó bảo. Jose không thể làm gì với một người luôn tìm kiếm sự chú ý và không tập trung vào việc chơi bóng. Các bạn có thể thấy ông ta tức giận với điều đó\", nguồn tin của Sportsmail tiết lộ.\r\n\r\n\"Rõ ràng, có những điểm tương đồng với mâu thuẫn giữa Beckham và Sir Alex trong cuộc đối đầu giữa những cái tôi và HLV không vui với tất cả những thứ đang xảy ra. Jose có thể bán cậu ta, nhưng CLB sẽ không cho phép. Ông ấy đã khiến các cầu thủ lạnh gáy hôm Chủ Nhật, nhưng với Pogba, mọi chuyện căng thẳng hơn. Sau những gì thể hiện ở trận gặp Man City, làm sao bạn có thể đá như thế trước West Brom?\", người này nói tiếp.\r\n\r\nBan lãnh đạo Man Utd hy vọng Mourinho và Pogba có thể hàn gắn quan hệ. Tuy nhiên, trong trường hợp mọi thứ đổ vỡ, đội chủ sân Old Trafford muốn bán tiền vệ người Pháp với giá 196 triệu đôla sau hai năm mua anh về với giá kỷ lục 119  triệu đôla. Không nhiều CLB đủ sức chiêu mộ Pogba. Người đại diện của cầu thủ này Mino Raiola được cho là đã liên hệ với PSG, Real, Barca, Man City và Chelsea trong những tháng gần đây.\r\n\r\nKhuôn mặt lạnh lùng của Mourinho khi thay Pogba ra khỏi sân ở trận gặp Bournemouth. Ảnh: AFP.\r\nKhuôn mặt lạnh lùng của Mourinho khi thay Pogba ra khỏi sân ở trận gặp Bournemouth. Ảnh: AFP.\r\n\r\nPogba trở nên cô lập trong phòng thay đồ của Man Utd khi những cầu thủ khác mệt mỏi vì những trò hề của anh. Một số người còn vui vì anh có thể không tham dự chuyến du đấu hè vì bận tham dự World Cup cùng tuyển Pháp. Pogba vẫn còn một số người bạn - Anthony Martial, Marouane Fellaini và Joel Pereira - những người anh thường ngồi ăn cùng.\r\n\r\nTuy nhiên, những cầu thủ từng rất thân với Pogba như Romelu Lukaku được cho là đã bớt thân hơn. Còn Jesse Lingard và Marcus Rashford thận trọng hơn khi ý thức về sự không hài lòng của Mourinho. Ngay cả mối quan hệ giữa Pogba và Zlatan Ibrahimovic trước khi chân sút người Thụy Điển ra đi cũng được cho là bớt thân thiết.\r\n\r\nTrong mắt những thành viên ban huấn luyện và nhiều cầu thủ Man Utd, Pogba là một cầu thủ đẳng cấp không thể duy trì sự ổn định ở mức cần thiết, một cầu thủ trẻ tin rằng bản thân đã được khẳng định. \r\n\r\nTuy nhiên, sự ra đi của Beckham 15 năm trước đã chứng minh không ai có thể để cái tôi lớn hơn Man Utd hay HLV của đội.', 'Thứ bảy, 21/4/2018 | 05:00 GMT+7', 'Minh Tuấn', 2, 0),
(9, 'HLV Park Hang-seo thăm Tuấn Anh tại bệnh viện Hàn Quốc', 'ta-2986-1524226495.jpg', 'Nhà cầm quân của tuyển Việt Nam đích thân động viên ngôi sao đội HAGL đang điều trị tại đây.\r\nHLV Park Hang-seo muốn cầu thủ U23 hạn chế các sự kiện ngoài bóng đá / HLV Park Hang-seo ghi điểm với cách dùng người trước Jordan\r\n\r\nÔng Park Hang-seo đang về Hàn Quốc để theo dõi U19 Việt Nam thi đấu tại giải giao hữu JS Cup. Tranh thủ dịp này, chiều 20/4, ông đã đến bệnh viện Wooridul tại  Seoul để thăm hỏi Tuấn Anh, học trò vừa trải qua ca phẫu thuật đầu gối.\r\n\r\nHLV Park nhắn nhủ tiền vệ người Thái Bình hãy luôn lạc quan, có tinh thần tốt để có thể hồi phục nhanh chóng. \"Cậu cứ yên tâm điều trị, tương lai của cậu còn rất dài phía trước. Tôi luôn quan tâm và chờ đợi cậu một khi cậu hồi phục\", nhà cầm quân của tuyển Việt Nam dặn dò Tuấn Anh.\r\n\r\nPark Hang-seo động viện Tuấn Anh tại bệnh viện. Ảnh: Lee Dong Jun.\r\nPark Hang-seo động viên Tuấn Anh tại bệnh viện. Ảnh: Lee Dong Jun.\r\n\r\nTuấn Anh từng được HLV Park Hang-seo triệu tập vào tuyển U23 Việt Nam trong quá trình chuẩn bị cho vòng chung kết giải vô địch U23 châu Á 2018 tại Trung Quốc. Tuy nhiên, anh sau đó bị trả về vì chấn thương.\r\n\r\nSau thời gian điều trị, Tuấn Anh trở lại thi đấu ở V-League. Tuy nhiên, tại vòng hai, tiền vệ tài hoa bậc nhất lứa đầu học viện HAGL Arsenal JMG này pha va chạm với đồng đội, tái phát chấn thương.\r\n\r\nĐể cứu vãn sự nghiệp của Tuấn Anh, bầu Đức quyết định đưa tiền vệ này sang Hàn Quốc điều trị. Êkip thực hiện ca mổ cho Tuấn Anh có bác sỹ Eun Sang Soo và bác sỹ nổi tiếng Choi Joo Yong, người từng chữa trị cho tiền vệ Park Ji Sung.\r\n\r\n\"Tuấn Anh đang được điều trị rất tốt, nhưng nhớ nhà và nhớ sân cỏ mỗi chiều cuối tuần\", trưởng đoàn HAGL Nguyễn Tấn Anh cho biết.\r\n\r\nCác bác sỹ tại Hàn Quốc cho biết, Tuấn Anh sẽ ở lại Seoul điều trị khi nào hoàn toàn bình phục mới trở lại Việt Nam.', 'Thứ bảy, 21/4/2018 | 00:00 GMT+7', 'Đức Đồng', 2, 0),
(10, 'Chủ tịch UEFA: \'VAR cũng vô dụng đối với trận Real - Juventus\'', 'uefa2-3948-1524103951.jpg', 'Chủ tịch Liên đoàn bóng đá châu Âu Aleksander Ceferin hạ thấp vai trò của công nghệ video.\r\nVidal nghỉ hết mùa, lỡ đại chiến với Real / Ronaldo đánh gót ghi bàn, cứu thua cho Real tại Bernabeu\r\n\r\nSau khi Juventus bị thổi quả phạt đền gây tranh cãi trong trận tứ kết lượt về với Real Madrid, bóng đá Italy đang kêu gọi sớm áp dụng công nghệ video VAR vào Champions League. Quan điểm đang nhận không ít ý kiến trái chiều, kể cả Chủ tịch UEFA.\r\n\r\n\"VAR sẽ không thay đổi được điều gì trong trận Real - Juventus. Có nhiều người đã xem tình huống đó đến 20 lần, mà một nửa nói đúng và một nửa vẫn nói không. VAR có thể sẽ hữu ích với trận Man City - Liverpool, nhưng sau đó có lẽ những vấn đề khác sẽ phát sinh\'\', Ceferin đánh giá.\r\n\r\nVị Chủ tịch người Slovenia, Aleksander Ceferin dẫn dắt UEFA từ năm 2016, sau khi Michel Platini ra đi. Ảnh: Reuters\r\nChủ tịch người Slovenia, Aleksander Ceferin dẫn dắt UEFA từ năm 2016, sau khi Michel Platini ra đi. Ảnh: Reuters.\r\n\r\nThủ quân của Juventus, Gianluigi Buffon từng lớn tiếng chỉ trích trọng tài Michael Oliver về tình huống thổi phạt đền, khi thời gian đá bù chỉ còn được tính bằng giây. Tỷ số khi đó đang là 3-0 nghiêng về Juventus, và chung cuộc là 3-3 sau hai lượt trận. Nếu không có quả phạt đền kể trên, hai đội sẽ thi đấu hiệp phụ. \r\n\r\n\"Những gì Buffon nói là không hợp lý. Từ góc nhìn của người chơi, tôi hiểu sự thất vọng của cậu ấy. Cậu ấy phát sầu vì thua ở phút 93\'\', nhà lãnh đạo UEFA nhận xét.\r\n\r\nVAR đã được áp dụng ở một số giải đấu nhưng còn gây nhiều tranh cãi. Công nghệ này gặp không ít vấn đề về cách xác định tình huống, chưa kể việc làm giảm hưng phấn và mất thời gian. Gần đây, các CLB Ngoại hạng Anh đã bỏ phiếu biểu quyết không áp dụng VAR cho mùa giải 2018-2019 để thử nghiệm thêm.\r\n\r\nVới các giải trên đấu trường châu Âu, UEFA cũng không tỏ ra vội vàng. Chủ tịch Ceferin cho biết: \'\'Tôi nghĩ đến mùa 2019-2020, chúng tôi mới dùng VAR cho Champions League và Europa League\'\'.', 'Thứ năm, 19/4/2018 | 10:19 GMT+7', 'Thanh Quý', 2, 0),
(11, '13 điều những người hạnh phúc không bao giờ làm', 'bat-hanh-4391-1524291492.jpg', 'Nếu hay làm những điều dưới đây hằng ngày, không có gì ngạc nhiên khi bạn luôn thấy mình bất hạnh.\r\n Những thói quen nên từ bỏ ở tuổi 30 để không hại đời về sau\r\n\r\nLo âu vì mỡ bụng\r\n\r\nTheo RD, những hình ảnh tràn ngập về hình dáng lý tưởng khiến đôi khi chúng ta tuyệt vọng khi nhìn về cơ thể mình. Nhưng chìa khóa để hạnh phúc không phải là thay đổi cơ thể bạn cho tới khi nó hoàn hảo mà hãy học cách yêu các nhược điểm, tiến sĩ tâm lý Erin Olivo, Đại học Columbia nói.\r\n\r\n\"Tôi yêu cầu các bệnh nhân của mình lập một danh sách những điều khiến một người nghĩ mình có giá trị và xứng đáng được yêu thương. Vẻ ngoài hầu như chưa bao giờ nằm trong danh sách đó. Nhưng khi tôi bảo họ liệt kê những thứ khiến họ nghĩ mình không xứng được hạnh phúc, vẻ ngoài lại đứng đầu danh sách\", ông kể. Vậy điều quan trọng cần nhận ra là: Hạnh phúc không phụ thuộc vào diện mạo.\r\n\r\n\"Chiến tranh lạnh\" với người khác\r\n\r\nKhi bị tổn thương, tự nhiên người ta muốn co mình lại nhưng những người bất hạnh thường hay trừng phạt người khác bằng sự lạnh lẽo và im lặng. Trong khi bí mật của hạnh phúc là sự trò chuyện. \"Ai cũng sợ bị ghét bỏ nhưng người hạnh phúc biết cách đòi hỏi được yêu thương\", tiến sĩ tâm lý Todd Pressman nói. \"Ngay cả khi không nhận được câu trả lời như ý, bạn sẽ phát hiện ra rằng nỗi sợ không được yêu chẳng hề khủng khiếp như mình nghĩ và điều đó có sức mạnh làm lành vết thương\", ông nói.\r\n\r\nLặp đi lặp lại các sai lầm\r\n\r\nAi cũng từng mắc lỗi nhưng khác biệt là ở sự học hỏi và tránh lặp lỗi cũ. Đa số chúng ta đeo bám mãi các thói quen xấu ngay cả khi biết chúng có hại cho mình về lâu dài. Muốn tiến xa, chỉ có cách vượt qua các rào cản, sửa những lỗi đã mắc.\r\n\r\nNhiều người tự khiến đời mình bất hạnh nhưng không nhận ra. Ảnh: Career Intelligence.\r\nNhiều người tự khiến đời mình bất hạnh nhưng không nhận ra. Ảnh: Career Intelligence.\r\n\r\nSợ thất bại tới nỗi không dám hành động\r\n\r\nCầu toàn và sợ thất bại là cặp bài trùng của những người bất hạnh. Những người hạnh phúc có thể cũng ngại thử điều mới hay mạo hiểm nhưng họ hiểu rằng chỉ vượt qua những thứ đó mới đến gần cơ hội thành công.\r\n\r\nTrì hoãn tới phút cuối\r\n\r\nTrì hoãn làm những điều khó có thể khiến bạn thấy thoải mái nhất thời nhưng về lâu dài sẽ có hại. \"Cam kết tập trung thực hiện một nhiệm vụ chỉ trong 15 phút, sau đó có thể giải lao nếu cần\", Hoan Do, huấn luyện viên cuộc sống và thành công khuyên. Thường, bạn sẽ không cần nghỉ mà tiếp tục làm việc sau khi vượt qua được sự chần chừ ban đầu.\r\n\r\nLuôn nghĩ mình là nạn nhân\r\n\r\nCó thể người khác có lỗi hay đối xử không tốt với bạn nhưng người hạnh phúc sẽ không thích vào vai nạn nhân. Họ không đổ lỗi cho người khác mà nhận trách nhiệm về mình bởi họ biết chính mình đưa ra lựa chọn và cũng có sức mạnh để giải quyết các vấn đề xảy ra.\r\n\r\nCoi nặng mọi ý kiến\r\n\r\nBạn không cần phải tin mọi lời người khác, nhất là khi họ nói về bạn. Hãy chỉ nhận lời khuyên từ những người bạn ngưỡng mộ và muốn đứng ở vị trí như họ. \r\n\r\nKhông đặt câu hỏi \"tại sao\" trước khi bắt tay vào việc\r\n\r\nĐã bao nhiêu lần bạn làm việc gì đó chỉ đơn giản bởi được yêu cầu làm thế? Người hạnh phúc hiểu mục đích sâu xa hơn trong những việc họ làm và kiên định với các mục tiêu. Từ đó, họ cũng dành trọn năng lượng vào việc đó, dễ dàng vượt qua các khó khăn và tận hưởng nhiều niềm vui hơn trong công việc, cuộc sống. \r\n\r\nMất hy vọng\r\n\r\nKhi gặp sóng gió, bạn có thể tập trung vào cơn bão đang vây quanh mình hoặc nhìn về ngày mai trời sáng lại. Cách thứ nhất sẽ khiến bạn cảm thấy mắc kẹt, đuối sức trong khi hướng thứ hai mang lại động lực để cố gắng, tiến lên. Đừng bao giờ từ bỏ hy vọng.\r\n\r\nCần được mọi người yêu mến\r\n\r\nGặp ai cũng được họ quý mến nghe có vẻ hay nhưng thực tế, nó là bằng chứng cho thấy bạn luôn muốn làm hài lòng mọi người và quá tập trung vào việc người khác nghĩ gì. Để hạnh phúc thực sự, bạn phải chấp nhận chính mình trước và cũng cần hiểu rằng việc có người không thích bạn là bình thường. \r\n\r\nKhông quan tâm tới sức khỏe của bản thân\r\n\r\nSức khỏe cơ thể và tâm trí không tách rời, vì thế nếu bạn muốn cảm thấy hạnh phúc thì cần chăm sóc bản thân tốt. Điều đó không có nghĩa là phải cố đạt các chuẩn như thân hình 6 múi, da láng căng... Thay vào đó, người hạnh phúc sẽ dành thời gian chăm sóc bản thân bằng cách ăn uống và tập luyện lành mạnh.\r\n\r\nQuên những niềm vui mình có\r\n\r\nMột trong những điều tốt nhất bạn có thể làm để lập tức cảm thấy hạnh phúc hơn là quên những điều mình muốn và tập trung vào việc biết ơn những điều mình đang có. Đây là một bí mật cho cuộc sống vui vẻ đã được các nhà khoa học chứng mình.\r\n\r\nBỏ qua danh sách những việc mình muốn làm trong đời\r\n\r\nTạo danh sách những nơi mong đến, những điều muốn học, những thành tựu khát khao đạt được... sẽ giúp bạn hình dung được cuộc đời mình muốn ra sao và phải làm gì để đạt tới. Nhiều người cả đời vẫn hoang mang vì bản thân họ vốn không biết mình muốn gì. \r\n\r\n', 'Thứ bảy, 21/4/2018 | 14:03 GMT+7', 'Vương Linh', 3, 0),
(40, 'Nhóm nhạc xinh đẹp gây chú ý trong đêm diễn có ông Kim Jong Un', '2017111614430146365.jpg', 'Red Velvet là nhóm nhạc thần tượng duy nhất trong đoàn nghệ sĩ Hàn Quốc đến Triều Tiên tham gia chương trình hòa nhạc đặc biệt, diễn ra từ ngày 1/4 đến 3/4. Trong đêm nhạc đầu tiên diễn ra tại Nhà hát lớn Đông Bình Nhưỡng vào ngày 1/4, Red Velvet biểu diễn hai ca khúc Red Flavour và Bad Boy. Trong đó, Red Flavour với giai điệu vui tươi, sôi động là một trong những bài hát được yêu thích nhất Kpop năm 2017. \r\nCa khúc trụ vững trong top 10 bảng xếp hạng Melon nhiều tháng liền. Bad Boy tuy không thành công về mảng nhạc số như Red Flavour nhưng cũng giúp nhóm thu hút thêm lượng fan lớn nhờ hình tượng quyến rũ, cá tính. Trực thuộc SM - một trong 3 công ty giải trí hàng đầu Hàn Quốc - Red Velvet sở hữu lượng fan đông đảo. Đó cũng là lý do giúp nhóm được mong chờ khi biểu diễn trong sự kiện quan trọng.\r\nTrong cuộc bình chọn của công chúng Triều Tiên diễn ra trước thềm đêm nhạc, Red Flavour và Bad Boy là 2 trong 5 ca khúc được mong đợi nhất. Biểu diễn với đội hình 4 thành viên do Joy vướng lịch trình cá nhân, không thể đến Triền Tiên nhưng nhóm nhạc nhà SM vẫn được khán giả cổ vũ nhiệt tình. Các thành viên tỏ ra bất ngờ và xúc động trước sự ủng hộ của khán giả. \"Họ vỗ tay to hơn chúng tôi tưởng tượng rất nhiều, họ còn hát theo nhóm nữa”, Yeri chia sẻ sau chương trình.', '16:02 02/04/2018', 'ZING.VN', 5, 1),
(13, 'Nhà 20 m2 ở Sài Gòn lắp đặt trong 3 tiếng', 'nha20-2-1524209072_680x0.jpg', 'Ngôi nhà sử dụng nội thất thông minh nên vẫn đủ các công năng dù diện tích nhỏ hẹp.\r\n Nhà tập thể 36 m2 như rộng gấp đôi sau khi sửa\r\n\r\nNhà 20 m2 ở Sài Gòn lắp đặt trong vòng 3 tiếng    \r\n \r\nNhà 20 m2 ở Sài Gòn lắp đặt trong 3 tiếng\r\nSau khi giới thiệu mẫu nhà gỗ 12 m2 được nhiều người yêu thích, một công ty ở Sài Gòn tiếp tục cho ra kiểu nhà rộng 20 m2 với nhiều tiện ích hơn. Căn nhà làm bằng gỗ thông với cấu trúc dạng vòm có đầy đủ các khu chức năng giống một căn hộ khép kín.\r\n\r\n \r\nNhà 20 m2 ở Sài Gòn lắp đặt trong 3 tiếng\r\nTheo anh Huỳnh Bảo Toàn, giám đốc công ty, nhà có thể tháo lắp thành các module, dễ dàng di chuyển tới các ngóc ngách, khu vực có giao thông hiểm trở. Việc lắp đặt nhà mất 3 tiếng đồng hồ, còn tháo dỡ chỉ tốn một tiếng.\r\n\r\n \r\nNhà 20 m2 ở Sài Gòn lắp đặt trong 3 tiếng\r\nNgôi nhà dùng nhiều nội thất thông minh như bàn có thể gập gọn vào kệ tivi, giường có ngăn kéo rộng để cất ghế, chăn gối, sofa biến thành giường đôi. Hệ thống điện nước đầy đủ nên gia chủ có thể đấu nối vào hệ thống điện lưới, ống cấp nước và hầm tự hoại để sử dụng luôn.\r\n\r\n \r\nNhà 20 m2 ở Sài Gòn lắp đặt trong 3 tiếng\r\nTổng chi phí để hoàn thiện cả căn nhà là 210 triệu đồng (trừ tivi, tủ lạnh). Hiện tại, công ty này nhận được 100 đơn hàng cho loại nhà 20-24 m2.\r\n\r\n \r\nNhà 20 m2 ở Sài Gòn lắp đặt trong 3 tiếng\r\nNhững người xây dựng nhà hy vọng sẽ tiếp tục ra thêm các mẫu công trình dùng pin năng lượng mặt trời, tái sử dụng nguồn nước thải.', 'Thứ bảy, 21/4/2018 | 09:26 GMT+7', 'Ban Mai', 3, 1),
(14, 'Chọn đồ nội thất có khả năng kháng cháy', 'kymdan-6327-1524133610.jpg', 'Chọn đồ nội thất có khả năng kháng cháy\r\nTrang bị hệ thống cửa, vách ngăn, đồ trang trí, nệm… có tính kháng cháy giúp tổ ấm của bạn tránh các sự cố cháy nổ.\r\nThời tiết hanh khô, nhu cầu sử dụng điện tăng cao gây chập điện hay sự bất cẩn của con người có thể dẫn đến nguy cơ hỏa hoạn.\r\n\r\nĐể bảo đảm sự an toàn cho gia đình, việc chọn sử dụng những sản phẩm, đặc biệt là đồ nội thất có tính kháng cháy cao quan trọng, vì giúp giảm khả năng bùng phát lửa khi đặt gần hoặc tiếp xúc với nguồn nhiệt.\r\n\r\nCửa\r\n\r\nGia đình có thể chọn loại cửa làm bằng gỗ có ngâm dung dịch và sơn chống cháy. Ảnh: Sankalp.\r\nGia đình có thể chọn loại cửa làm bằng gỗ có ngâm dung dịch và sơn chống cháy. Ảnh: Sankalp.\r\n\r\nĐóng vai trò như lối thoát trong ngôi nhà, khi mua cửa, gia đình có thể lựa chọn các vật liệu có tính năng kháng cháy mà vẫn đảm bảo tính thẩm mỹ trong thiết kế.\r\n\r\nChất liệu thép chống cháy có thể giảm thiểu sự lan tỏa của lửa hay ngăn cản khói, giúp bạn có thêm thời gian để thoát ra ngoài. Cửa có tính năng đóng mở tự động khi xảy ra cháy nổ cũng là một lựa chọn. Loại cửa này dùng phổ biến trong nhà xưởng, khu công nghiệp, trung tâm thương mại….\r\n\r\nNếu muốn mua cửa với chất liệu gỗ, bạn cần cân nhắc chọn loại cửa làm bằng gỗ có ngâm dung dịch và sơn chống cháy. Cánh cửa cấu tạo gồm nhiều lớp, trong đó phần khoảng trống của khung xương nhồi bằng sợi bông thủy tinh và các tấm chống cháy sẽ giúp tăng độ chịu đựng của cửa trước ngọn lửa.\r\n\r\nVách ngăn\r\n\r\nMột căn hộ sử dụng tường thạch cao. Ảnh: Sant Gobain VN.\r\nMột căn hộ sử dụng tường thạch cao. Ảnh: Sant Gobain VN.\r\n\r\nCác vách ngăn làm bằng vật liệu kháng cháy sẽ ngăn cách các khu vực trong nhà, làm giảm lan tỏa của đám cháy. Không chỉ giúp hạn chế hủy hoại ngôi nhà, vách ngăn còn kéo dài thời gian di tản hoặc tìm được nơi an toàn.\r\n\r\nTheo tiêu chuẩn BS 476 của Anh trong “Xác định khả năng chống cháy của vách” thì một hệ vách được coi là có khả năng chống cháy khi thỏa mãn các yêu cầu kỹ thuật: duy trì tính toàn vẹn của cả hệ thống và có khả năng cách nhiệt tốt.\r\n\r\nBạn có thể chọn vật liệu làm vách với thành phần lõi là thạch cao chịu lửa hoặc các các chất không duy trì sự cháy như magie, cal silicate…\r\n\r\nĐồ trang trí\r\n\r\nGia đình có thể chọn vật liệu đồ trang trí bằng thủy tinh, sứ... Ảnh: Văn Hữu.\r\nGia đình có thể chọn vật liệu đồ trang trí bằng thủy tinh, sứ... Ảnh: Văn Hữu.\r\n\r\nXuất hiện hầu như ở mọi ngóc ngách của ngôi nhà, các đồ trang trí đóng một phần quan trọng tạo nên nét thẩm mỹ cho không gian. Để nâng cao tính an toàn, bạn có thể lựa chọn sản phẩm làm từ nguyên vật liệu không dễ bắt lửa như gốm, sứ, thủy tinh….\r\n\r\nVới các sản phẩm làm từ vải, gia đình có thể lựa chọn sợi thủy tinh có khả năng chịu nhiệt cao hơn so với các loại vải thông thường.\r\n\r\nVải chống cháy được sản xuất bằng cách phủ hóa chất lên sợi hoặc sợi được sản xuất một cách đặc biệt để có thể chịu được lửa để chống cháy. Các loại vải làm rèm kháng cháy cũng giúp giảm khả năng lan truyền ngọn lửa khi mới xảy ra cháy nhờ khả năng bắt lửa lâu hơn.\r\n\r\nNệm\r\n\r\nNệm Kymdan kháng cháy không bắt lửa với than củi nung đỏ tại thời điểm nhiệt độ đạt 1.000 độ C trong 7 phút.\r\nNệm Kymdan kháng cháy không bắt lửa với than củi nung đỏ tại thời điểm nhiệt độ đạt 1.000 độ C trong 7 phút. Xem video than cháy đỏ đặt lên nệm Kymdan tại đây.\r\n\r\nKhông riêng gì gian bếp hay phòng khách, phòng ngủ cũng có nguy cơ là nơi bắt nguồn cháy. Không gian sinh hoạt, nghỉ ngơi của bố mẹ và con cái thường ở trên chiếc giường nệm êm ái. Tuy nhiên, các hành vi bất cẩn như để tàn thuốc rơi xuống nệm, để quên bàn ủi, sạc pin kém chất lượng, dễ phát tia lửa điện… thường là nguyên nhân khiến lửa bùng phát. Nệm thông thường dễ bén lửa và phát cháy. Bởi vậy, gia đình có thể cân nhắc lựa chọn loại sản phẩm có khả năng kháng cháy để đảm bảo hơn sự an toàn của các thành viên.\r\n\r\nCác tính năng này phải được kiểm tra khắt khe và chứng nhận bởi các đơn vị uy tín. Tiêu chuẩn kháng cháy 16 CFR 1632 và 16 CFR 1633 của Mỹ quy định những yêu cầu cho thử nghiệm khả năng kháng cháy của nệm trước khi giới thiệu, bán ra thị trường với mục đích giảm thiệt hại về người liên quan đến các vụ cháy nệm.\r\n\r\nTheo đại diện Kymdan, hãng nệm Việt duy nhất được nhập khẩu và lưu hành trên toàn nước Mỹ từ năm 2004 đến nay, để đạt được chứng nhận kháng cháy, sản phẩm nệm của công ty đã trải qua các cuộc kiểm nghiệm khả năng kháng cháy tại phòng thí nghiệm Bureau Veritas, Mỹ.\r\n\r\nVới nệm Kymdan có tính chất kháng cháy sẽ không bắt lửa với bàn ủi để quên không ngắt điện, nhiệt độ 295 độ C liên tục 60 phút.\r\nVới nệm Kymdan có tính chất kháng cháy sẽ không bắt lửa với bàn ủi để quên không ngắt điện, nhiệt độ 295 độ C liên tục 60 phút. Xem video đặt bàn ủi nóng lên nệm Kymdan tại đây.\r\n\r\nNệm có tính kháng cháy cao không bắt lửa với bàn ủi để quên không ngắt điện, tại thời điểm nhiệt độ đạt 295 độ C, liên tục một giờ đồng hồ. Khi đặt than củi nung đỏ có độ nóng đạt 1.000 độ C trong 7 phút hay sử dụng hạt kim loại nhiệt độ cao phóng ra từ tia lửa hàn điện có cường độ 155A trong 2 phút, cũng không có dấu hiệu bắt lửa ở nệm.\r\n\r\nNệm Kymdan không bắt lửa với những hạt kim loại ở nhiệt độ cao được phóng ra từ tia lửa hàn điện (hồ quang), khoảng cách giữa mặt tấm nệm và mặt thanh hàn là 10cm, khởi động máy hàn có cường độ dòng diện là 155A liên tục trong 2 phút.\r\nNệm Kymdan không bắt lửa với những hạt kim loại ở nhiệt độ cao được phóng ra từ tia lửa hàn điện (hồ quang), khoảng cách giữa mặt tấm nệm và mặt thanh hàn là 10cm, khởi động máy hàn có cường độ dòng diện là 155A liên tục trong 2 phút. Xem video nệm Kymdan phản ứng với tia lửa điện nhiệt độ cao tại đây.\r\n\r\nGia đình cũng nên chọn loại nệm có cấu trúc bọt hở, khả năng thấm hút nhanh sẽ dễ dàng dập tắt bằng nước. Nệm kháng cháy tốt từ bản chất vật liệu. Nếu chỉ phủ bên ngoài một lớp mỏng chất kháng cháy sẽ không thể bảo đảm khả năng giảm thiểu nguy cơ hỏa hoạn trong suốt thời gian sử dụng.\r\n\r\nĐại diện công ty nệm Kymdan khẳng định, tính chất kháng cháy thể hiện ở yếu tố làm chậm sự cháy, không thuận lợi để duy trì và gia tăng sự lan tỏa của lửa.', 'Thứ sáu, 20/4/2018 | 09:00 GMT+7', 'Vân Trương', 3, 0),
(15, 'Phát hiện vô sinh sau khi khám nam khoa dù có tới 9 đứa con', 'male-swimmers-750x500-6904-1524128862.jpg', 'Một người đàn ông Maroc phát hiện bị u tinh hoàn ít nhất đã 50 năm nên hoàn toàn không có tinh trùng dù cưới vợ và sinh 9 con.\r\nĐàn ông phương Tây chi bộn tiền để tăng lượng tinh trùng\r\n\r\nTheo OddityCentral, người đàn ông này đến bệnh viện khám sức khỏe định kỳ. Bác sĩ bất ngờ phát hiện ông có một khối u nang nhỏ ở tinh hoàn bên phải. Tiến hành xét nghiệm thêm, bác sĩ cho biết khối u đã xuất hiện từ rất lâu rồi. Nó không đe dọa đến tính mạng, nhưng là nguyên nhân khiến người đàn ông này bị vô sinh.\r\n\r\nChẩn đoán của bác sĩ khiến người đàn ông đang sống với gia đình ở thành phố Sidi Slimane bị sốc. Bởi lẽ, ông có đến 9 người con trong cuộc hôn nhân hạnh phúc kéo dài suốt 35 năm qua với vợ.\r\n\r\nẢnh: Alexander Klepnev.\r\nẢnh: Alexander Klepnev.\r\n\r\nKhông tin tưởng vào kết quả này, người đàn ông đi kiểm tra lại. Ông yêu cầu bệnh viện thực hiện nhiều xét nghiệm khác. Kết quả cuối cùng phát hiện ông bị vô sinh ít nhất 50 năm qua. Quá sốc, người đàn ông đã liên hệ với luật sư để kiện vợ tội ngoại tình, làm thủ tục ly hôn và từ bỏ trách nhiệm với 9 người con.\r\n\r\nTòa đã lập một nhóm chuyên gia để kiểm tra lại các kết quả xét nghiệm của người đàn ông. Họ khẳng định rằng các bằng chứng khoa học từ kết quả xét nghiệm hoàn toàn đúng. Ông hoàn toàn không có tinh trùng.\r\n\r\nỞ Maroc, ngoại tình có thể bị xét xử hình sự và ở tù. Người đàn ông cũng cắt đứt quan hệ với 9 đứa con và yêu cầu miễn trách nhiệm cũng như những ràng buộc pháp lý.\r\n\r\nTheo Allcysts, u nang tinh hoàn ở nam giới có nhiều nguyên nhân. Đáng chú ý là bệnh này không chỉ có ở người trưởng thành mà còn cả thanh thiếu niên, cũng như những người già. Nguyên nhân gây u nang tinh hoàn có thể do viêm ở cơ quan sinh dục như viêm niệu đạo, viêm tuyến tiền liệt... Ngoài ra còn do sự hiện diện của các bệnh nhiễm trùng trong cơ thể chủ yếu lây truyền qua quan hệ tình dục như bệnh lậu, giang mai, chlamydia. Chấn thương cơ bìu cũng là một trong những nguyên nhân gây u nang tinh hoàn.\r\n\r\nTheo các chuyên gia, triệu chứng đầu tiên của u nang tinh hoàn là đau dai dẳng ở tinh hoàn. Một số biểu hiện khác như nóng trong người, buồn nôn, suy nhược. Trường hợp không điều trị kịp thời có thể nhanh chóng leo thang hành dạng mãn tính.\r\n\r\nBệnh nhân u nang tinh hoàn cần điều trị khá phức tạp, được sử dụng liệu pháp thuốc và thay đổi chế độ ăn. Trong một số trường hợp, bệnh nhân cần phẫu thuật cắt bỏ.', 'Thứ năm, 19/4/2018 | 11:07 GMT+7', 'Thu Hiền', 3, 0);
INSERT INTO `tintuc` (`id`, `tieuDe`, `hinhAnh`, `noiDung`, `time`, `author`, `idTL`, `tinnoibat`) VALUES
(16, 'Lao đao vì scandal, phải chăng “nữ hoàng thời trang sân bay” Dương Mịch sắp bị Địch Lệ Nhiệt Ba soán ngôi?', 'nhiet3sogirl.jpg', 'Sự nghiệp của Địch Lệ Nhiệt Ba đang lên như diều gặp gió, trong khi Dương Mịch lại tụt dốc không phanh vì scandal quỵt tiền từ thiện.\r\nTừ “nữ thần quảng cáo” Dương Mịch bỗng bị nhãn hàng ghẻ lạnh, xóa bỏ hình ảnh sau scandal quỵt tiền từ thiện \r\nChẳng phải nội dung, người ta chỉ quan tâm đến màu son và mái tóc của Dương Mịch trong phim mới \r\nPhim thì xịt nhưng style của Dương Mịch trong \"Người đàm phán\" lại được fan thích mê \r\nScandal quỵt tiền từ thiện lần này có thể xem là 1 trong scandal lớn nhất trong sự nghiệp của Dương Mịch. Cô bị cư dân mạng tẩy chay, các báo lớn lên tiếng phê phán, nhãn hàng còn xóa bớt hình ảnh quảng cáo của bà mẹ 1 con. Dù đã gần 1 tháng trôi qua nhưng vụ lùm xùm lần này vẫn chưa có dấu hiệu chấm dứt, từ \"nữ hoàng thời trang sân bay\" giờ đây Dương Mịch chỉ che mặt kín mít, đi thật nhanh mỗi khi bắt gặp cánh phóng viên.\r\n\r\nLao đao vì scandal, phải chăng “nữ hoàng thời trang sân bay” Dương Mịch sắp bị Địch Lệ Nhiệt Ba soán ngôi? - Ảnh 1.\r\nTừ \"nữ hoàng thời trang sân bay\" sành điệu...\r\n\r\nLao đao vì scandal, phải chăng “nữ hoàng thời trang sân bay” Dương Mịch sắp bị Địch Lệ Nhiệt Ba soán ngôi? - Ảnh 2.\r\n... Dương Mịch giờ đây chỉ che mặt kín mít, không còn tâm trạng \"lên đồ\" sành điệu như mọi khi.\r\n\r\nLao đao vì scandal, phải chăng “nữ hoàng thời trang sân bay” Dương Mịch sắp bị Địch Lệ Nhiệt Ba soán ngôi? - Ảnh 3.\r\nDương Mịch cũng né tránh ống kính của phóng viên, thân hình gầy gò của cô cũng khiến nhiều fan hâm mộ xót xa.\r\n\r\nTrong khi sự nghiệp của Dương Mịch đang có dấu hiệu tuột dốc không phanh, thì đàn em của cô – Địch Lệ Nhiệt Ba lại lên như diều gặp gió. Thậm chí nhiều người cho rằng: có lẽ rất nhanh trong tương lai, Địch Lệ Nhiệt Ba sẽ nhanh chóng soán ngôi \"nữ hoàng thời trang sân bay\" của Dương Mịch cũng không biết chừng. \r\n\r\nLao đao vì scandal, phải chăng “nữ hoàng thời trang sân bay” Dương Mịch sắp bị Địch Lệ Nhiệt Ba soán ngôi? - Ảnh 4.\r\nTrong lần gần đây nhất ra sân bay, Địch Lệ Nhiệt Ba lại \"gây bão\" vì nhan sắc xinh đẹp và gu thời trang sành điệu khoe khéo đôi chân dài gợi cảm.\r\n\r\nLao đao vì scandal, phải chăng “nữ hoàng thời trang sân bay” Dương Mịch sắp bị Địch Lệ Nhiệt Ba soán ngôi? - Ảnh 5.\r\nKể từ khi mới chân ướt chân ráo bước vào nghề, Địch Lệ Nhiệt Ba đã được ca ngợi vì nhan sắc xinh đẹp. Nãng mỹ nữ Tân Cương sở hữu ngoại hình vừa ngây thơ ngọt ngào lại sang chảnh quyến rũ. Tuy sự nghiệp diễn xuất của cô chưa có gì nổi trội, nhưng mảng thời trang lại phát triển vô cùng nhanh chóng. Đặc biệt là màn catwalk xuất thần trên sàn diễn của Dolce & Gabbana tại Milan Fashion Week đã giúp cô nhận được nhiều khen ngợi. \r\n\r\nLao đao vì scandal, phải chăng “nữ hoàng thời trang sân bay” Dương Mịch sắp bị Địch Lệ Nhiệt Ba soán ngôi? - Ảnh 6.\r\nTiếp sau đó, Địch Lệ Nhiệt Ba còn trở thành đại sứ của Dolce & Gabbana tại khu vực Châu Á – Thái Bình Dương. Trong khi đó, Dương Mịch dù là đại diện toàn cầu của Michael Kors, nhưng vị trí này vẫn bị đánh giá là thấp kém hơn so với vị trí đại sứ của Địch Lệ Nhiệt Ba. \r\n\r\nLao đao vì scandal, phải chăng “nữ hoàng thời trang sân bay” Dương Mịch sắp bị Địch Lệ Nhiệt Ba soán ngôi? - Ảnh 7.\r\nĐịch Lệ Nhiệt Ba đang là đại sứ khu vực Châu Á – Thái Bình Dương của Dolce & Gabbana.\r\n\r\nLao đao vì scandal, phải chăng “nữ hoàng thời trang sân bay” Dương Mịch sắp bị Địch Lệ Nhiệt Ba soán ngôi? - Ảnh 8.\r\nDù là đại sứ toàn cầu, nhưng Michael Kors của Dương Mịch không thể sánh được sự sang chảnh của Dolce & Gabbana.\r\n\r\nVề thời trang sân bay, từ trước đến nay gu thời trang của Địch Lệ Nhiệt Ba vẫn được đánh giá cao vì sự tiện dụng, đẹp mắt. Với lợi thế nhan sắc hơn người, lại thêm vóc dáng đẹp và đôi chân dài miên man, Địch Lệ Nhiệt Ba dễ dàng chinh phục nhiều kiểu trang phục khác nhau. Mỗi lần ra sân bay cô lại trở thành tâm điểm chú ý của người hâm mộ. Chính vì lẽ đó, rất có thể trong tương lai, nàng tiểu hoa đán này sẽ nhanh chóng soán ngôi \"nữ hoàng\" của người đàn chị.', '22:00 21/04/2018', 'Tôn Lượng', 4, 1),
(35, 'Dù là thời trang thảm đỏ hay phong cách thường ngày, nàng mỹ nữ Tân Cương này cũng dần trở nên xinh đẹp, xao xuyến hút hồn.', 'coluc.jpg', 'Địch Lệ Nhiệt Ba và Cổ Lực Na Trát là 2 mỹ nữ Tân Cương nổi bật nhất hiện nay của làng giải trí Hoa ngữ. Nếu như Địch Lệ Nhiệt Ba sở hữu nhan sắc thanh thuần với gu thời trang năng động, khỏe khoắn; thì Cổ Lực Na Trát lại trái ngược hoàn toàn, cô nàng sở hữu nét đẹp sắc sảo, lạnh lùng vậy nhưng lại chỉ chuyên diện những thiết kế mong manh, yểu điệu, thậm chí còn có phần khá mờ nhạt, thiếu cá tính.', '22:22 22/01/2018', 'TRÍ THỨC ', 4, 0),
(34, 'Phạm Hương là nàng Hoa hậu thay đổi phong cách chóng mặt nhất Vbiz', 'phamhuong.jpg', 'Hôm qua Phạm Hương mới cá tính thì hôm nay đã diện váy ngọt ngào đúng chuẩn Hoa hậu.\r\nSau khi úp mở về show thời trang cá nhân cũng như việc rẽ hướng sang lĩnh vực mới, Phạm Hương đã khiến giới mộ điệu thời trang khá bất ngờ trước quyết định táo bạo này. Vừa qua, tuy khá bận rộn với lịch trình, cô đã đích thân tổ chức buổi tuyển chọn người mẫu cho show thời trang “Girl Boss” diễn ra vào ngày 11/5 của mình.\r\nMỹ nhân họ Phạm năm nay hoạt động sôi nổi trong lĩnh vực thời trang, cô không gò bó mình vào một quy chuẩn nào mà luôn thay đổi phong cách mỗi khi xuất hiện. \r\n\r\nMới vừa tham gia show diễn của NTK Lê Thanh Hòa trong vị trí vedette, với hình tượng nữ thần mặt trời. Thì hôm sau đã thấy người đẹp cá tính với bộ suit cách điệu họa tiết sọc cùng màu son môi thâm cá tính. Hay là set đồ sơ mi trẻ trung như nữ sinh.', 'Ngày 24/04/2018 16:40 PM', 'Zing.vn', 4, 1),
(31, 'Mê mẩn ngắm vóc dáng tuyệt mỹ của thiên thần làng mẫu Brazil', 'marina-nery-1524628345-708-width665height449.jpg', 'Marina Nery được mệnh danh là \"thiên thần\" của làng thời trang Brazil bởi gương mặt xinh đẹp và vóc dáng nuột nà không tì vết.', 'Thứ Tư, ngày 25/04/2018 11:21 AM (GMT+7)', 'TRÂN TRÂN', 4, 0),
(21, 'Ước mơ gặp thần tượng Hàn Quốc không còn xa, bạn chỉ cần có 300k!', 'img20171219153458756.jpg', 'Nếu là một fan Kpop chân chính, hẳn bạn đã biết đến giải thưởng Seoul Music Awards thường quy tụ những ngôi sao lớn như EXO, TWICE, Red Velvet, BTS...\r\nChỉ với 300.000 VND, hãy bắt đầu “Săn vé gặp idol” cùng Music Lovin’ Journey của PNJSilver để được đến Hàn Quốc 5 ngày 4 đêm và tham dự SMA, bạn có muốn thử?\r\n\r\nSeoul Music Awards – giấc mơ của mọi tín đồ Kpop\r\n\r\nSeoul Music Awards là một giải thưởng được thành lập từ năm 1990 và được trao hàng năm bởi Sports Seoul cho những thành tựu đặc biệt trong ngành công nghiệp âm nhạc Hàn Quốc. Người chiến thắng là nghệ sĩ đã phát hành album trong năm đó, cùng với 20% bình chọn thông qua di động, 10% trong cuộc khảo sát ý kiến của Sports Seoul, 40% lượt tải đĩa nhạc kĩ thuật số và album bán ra, 30% số điểm của giám khảo.\r\n\r\nƯớc mơ gặp thần tượng Hàn Quốc không còn xa, bạn chỉ cần có 300k! - Ảnh 1.\r\nSeoul Music Awards là giải thưởng âm nhạc danh giá tại Hàn Quốc\r\n\r\nSeoul Music Awards là một trong 4 giải thưởng lớn nhất được trao hàng năm trong ngành công nghiệp âm nhạc Hàn Quốc cùng với Melon Music Awards, Mnet Asian Music Awards và Golden Disk Awards. Có thể nói, được tham dự Seoul Music Awards với một fan Kpop cũng giống như lạc vào thiên đường vì bạn sẽ cơ hội tận mắt gặp gỡ những ngôi sao, nhóm nhạc hàng đầu tại Hàn Quốc như Big Bang, BTS, EXO, Red Velvet, TWICE... và theo dõi những màn trình diễn công phu, đặc sắc của họ.\r\n\r\n \r\n\r\nƯớc mơ gặp thần tượng Hàn Quốc không còn xa, bạn chỉ cần có 300k! - Ảnh 2.\r\nCác nhóm nhạc nhận giải tại Seoul Music Awards lần thứ 26 vừa qua\r\n\r\nTham dự Seoul Music Awards và du lịch Hàn Quốc chỉ với 300.000 VND!\r\n\r\nƯớc mơ gặp idol giờ đây không còn xa nữa! Như một cách kết nối các con tim yêu âm nhạc, PNJSilver dành tặng 5 vé tham dự Seoul Music Awards lần thứ 27 trong chuyến du lịch Hàn Quốc 5 ngày 4 đêm. Không chỉ tham dự Seoul Music Awards, bạn còn có cơ hội tham quan SM fan mall – trường đào tạo các idol để tìm hiểu quá trình luyện tập, con đường theo đuổi ước mơ của các thần tượng. Ngoài ra, bạn còn có thể tham quan đảo Nami – bối cảnh của nhiều cảnh quay nổi tiếng trong bộ phim Bản tình ca mùa đông và các địa điểm nổi tiếng tại Seoul. Tất cả hành trình đó sẽ được ekip chuyên nghiệp của PNJSilver ghi hình lại để bạn lưu giữ lại những hình ảnh của chuyến đi như một kỷ niệm đáng nhớ.\r\n\r\nƯớc mơ gặp thần tượng Hàn Quốc không còn xa, bạn chỉ cần có 300k! - Ảnh 3.\r\nThành phố Seoul sôi động\r\n\r\nƯớc mơ gặp thần tượng Hàn Quốc không còn xa, bạn chỉ cần có 300k! - Ảnh 4.\r\nMusic Lovin’ – bộ sưu tập mới nhất của PNJSilver lấy cảm hứng từ âm nhạc\r\n\r\nĐể săn vé gặp idol thành công, bạn chỉ cần mua hàng tại hệ thống các cửa hàng của PNJSilver với hóa đơn từ 300.000 VND từ ngày 15/12/2017 đến 24/12/2017. Với mỗi hóa đơn 300.000 VND, bạn sẽ nhận được 1 mã dự thưởng tham gia chương trình Music Lovin’ Journey của PNJSilver - Săn vé gặp thần tượng. Cơ hội lên đến 5 giải, nhanh tay tham gia để đến Hàn Quốc bạn nhé!', '20:30 19/12/2017', 'TRÍ THỨC', 5, 0),
(22, '7 thương hiệu công ty Hip Hop hàng đầu Kpop trong thời điểm hiện tại', '2-f097.jpg', 'Cùng điểm danh 7 thương hiệu Hip hop nổi tiếng nhất của nền âm nhạc xứ kim chi nhé!\r\nVào những năm 90 trước đây , Hip-hop thực sự là một phần không thể thiếu trong làn sóng âm nhạc Hàn Quốc. Trong vài năm gần đây, làn sóng này đã thật sự trở lại và trở lại theo cách mạnh mẽ hơn bao giờ hết với sự xuất hiện của nhiều công ty, nhiều thương hiệu riêng được tạo nên bởi các nghệ sĩ Hip hop kì cựu trong ngành giả trí. \r\n\r\nNgay cả trước khi các nghệ sĩ hip hop tham gia phổ biến vào ngành sản xuất âm nhạc, công chúng đã có dịp biết đến có những thương hiệu  như Amoeba Culture, Brand New Music, và nhiều hơn nữa. Ví dụ công ty Hi-Lite được rapper Paloalto thành lập vào năm 2010 - anh là nhà sản xuất cho chương trình \"Show Me The Money 4\". Khi hip hop ở Hàn Quốc phát triển mạnh, công ty sau đó đã được mua lại bởi CJ E & M, và được mở rộng để Hi-Lite Records tập trung làm nhạc.\r\n\r\nSự phổ biến của thể loại này đã tạo ra nhiều thương hiệu đại diện cho hip hop của Hàn Quốc, với sự gia tăng các nhóm nhảy nghiệp dư, các nghệ sĩ solo nổi tiếng và sự thành công của các chương trình tìm kiếm, phát triển tài năng hip hop như \"Show Me The Money\" và \"Unpretty Rapstar\". Đó là những lý do để hip hop Hàn Quốc tiếp tục phát triển đến tận bây giờ.\r\n\r\nDưới đây là danh sách bảy thương hiệu công ty Hip-hop nổi tiếng nhất trong thời điểm hiện tại, và họ đang tiếp tục phát triển không ngừng để đưa Hip hop tiến xa tiến sâu hơn đến thị trường thế giới:\r\n\r\n1. Feel Ghood Music\r\n\r\nFeel Ghood Music là công ty hip hop thứ hai của \"ông hoàng hip hop\" Tiger JK sau công ty Jungle Entertainment - được ra đời vào năm 2006. Trong năm 2013, rapper đã tham gia cùng với các thành viên trong nhóm MFBTY của mình là Yoon Mirae và Bizzy để tạo ra một nhãn hiệu mới. Mặc dù không bao giờ đề cập đến lý do thực sự đằng sau sự ra đi của họ ở công ty đầu tiên, nam ca sĩ bày tỏ mong muốn đưa một nhóm nhỏ gồm những người bạn đáng tin cậy và các thành viên trong gia đình vào công ty mới của mình.\r\n\r\nFeel Ghood Music chào đón sự góp mặt cựu thí sinh của chương trình truyền hình \"Show Me The Money 5\" - Junoflo và bày tỏ sự hứng thú về việc tạo nên một xu hướng âm nhạc sôi động hơn với sự bổ sung mới này.  Hơn nữa, Tiger JK và Bizzy cũng đang tham gia \"Show Me The Money 6\" với tư cách là nhà sản xuất.\r\n\r\nThông qua thương hiệu mới, MFBTY bắt tay hợp tác với Lee Hyori tại MBC Gayo Daejaejeon năm 2013. Tiger JK thậm chí còn là người viết giai điệu cho bài hát \"Miss Korea\" của Lee Hyori. Một sự hợp tác đáng nhớ khác là  ca khúc \"Buckubucku\",  trong đó có sự kết hợp giữa Rap Monter của BTS cùng với rapper EE và Dino-J.\r\n\r\nĐoạn video dưới đây cũng là một phần giới thiệu cho việc phát hành album \"Wondaland\" của họ.\r\n\r\n\r\n\r\n2. Công ty Vismajor\r\n\r\n\r\n\r\nCông ty bắt đầu hoạt động vào năm 2011 do Deepflow, Babynine, Wutan và VEN thành lập. Công ty đã đi vào hoạt động hoàn thiện vào năm 2014 với tổng cộng 12 nhạc sĩ và các ca sĩ, rapper. Trong số đó DJ. Nucksal, Don Mills và Odee là một trong những rapper đầy hứa hẹn, tất cả họ đều tham gia thử giọng cho chương trình \"Show Me The Money.\" Trong khi Odee không may mắn nằm trong top 16 của \"Show Me The Money 3\", thì hiện các đồng nghiệp của anh ấy đang chờ đợi kết quả thử giọng của họ trong chương trình \"Show Me The Money 6\" được phát sóng vào ngày 30 tháng 6.\r\n\r\nDon Mills đã xuất hiện trong đĩa đơn đầu tay chính thức của Zico  mang tên \"Tough Cookie\". Ca khúc thu về giải thưởng \"Bài hát rap tốt nhất thuộc hệ thống bình chọn và trao giải KMC của Pháp vào năm 2014\" (KMC là viết tắt của Korean Music and Communication). Anh cũng là một giảng viên dạy rap trong chương trình  của Mnet \"Produce 101 Season 2.\"', 'Thứ tư, 05/07/2017 14:02', 'Thu Trang', 5, 0),
(23, 'Yeri (Red Velvet) tiết lộ \'trạng thái quan hệ\' của bản thân sau khi bị nghi ngờ hẹn hò Jungkook (BTS)', '20180421142528-f063.jpg', 'Em út Red Velvet là người không quan tâm đến ngoại hình của đối phương và mẫu người lý tưởng của cô nàng là hai diễn viên lớn hơn Yeri nhiều tuổi.\r\nCách đây ít lâu, tin đồn em út Yeri (Red Velvet) và Jungkook (BTS) đang trong mối quan hệ hẹn hò đã khiến netizen tranh cãi dữ dội. Xuất phát từ việc cả hai cùng tung bản cover ca khúc của tiền bối một cách trùng hợp. Jungkook đã thể hiện bài hát \"All Of My Life\" một cách ngọt ngào, còn Yeri cover lại \"Only Then\" của  Roy Kim. \r\n\r\n\r\n\r\nSự kiện khiến Jungkook và Yeri vướng tin đồn hẹn hò \r\n\r\nNghi ngờ hai thần tượng này có gì đó mờ ám, fan K-pop đã bắt tay vào tìm hiểu. Thì ra Jungkook và Yeri đã có một số lần cười và chào hỏi nhau ở hậu trường. Chiếc áo khoác ngoài của Yeri giống y hệt áo mà Jungkook đã từng diện ra sân bay. Tuy người trong cuộc không lên tiếng xác nhận nhưng fan K-pop chắc mẩm: \"90% là hẹn hò rồi!\". Cả Red Velvet và BTS đều là những idol hàng top đang nổi tiếng, vì vậy nhiều ARMY đã tấn công Yeri trên mạng xã hội. \r\n\r\n\r\n\r\nBằng chứng hẹn hò mà cư dân mạng tìm ra\r\n\r\nMới đây, Yeri đã tham gia show giải trí \"Secret Unnie\". Ngay từ tập 1, em út Red Velvet đã đề cập đến mẫu hình người đàn ông lý tưởng của mình. Yeri nói:\r\n\r\n\"Thât lòng thì em không để ý đến ngoại hình của đối phương đâu. Mẫu người lý tưởng của em chỉ có tiền bối Jo In Sung mà thôi...Bên cạnh đó, em còn thích anh Kang Dong Won nữa ạ. Ngoài hai anh ấy ra, em-không-thích-bất-kỳ-nghệ-sĩ-nam-nào-hết!\"\r\n\r\n\r\n\r\nfgn\r\n\r\nHai nghệ sĩ nam mà Yeri yêu mến là Jo In Sung và Kang Dong Won\r\n\r\nYeri khẳng định như đinh đóng cột rằng mình không quan tâm bất kỳ ai khác ngoài Jo In Sung và Kang Dong Won. Có vẻ như em út Yeri thích những người khác phái lớn tuổi, chín chắn chứ không hề hứng thú với các bạn nam trạc tuổi:\"Em thích một người tốt bụng, có trái tim ấm áp, trí nhớ tốt, không quá nhạy cảm và giống với em\". \r\n\r\nKhi được hỏi về việc hiện tại đã có bạn trai hay chưa, Yeri thẳng thắn trả lời:\"Sự thật là hiện giờ em chẳng có người bạn trai nào đâu ạ!\"', 'Thứ bảy, 21/04/2018 17:22', 'HL', 5, 0),
(24, 'Đoàn làm phim \'My Ajusshi\' tìm được một diễn viên nhí xinh đẹp có ngoại hình giống hệt IU', '20180421170931-a0c2.jpg', 'Đây đích thực là bản sao chuẩn nhất của IU vì có sự tương đồng rõ rệt về ngoại hình.\r\nSự xuất hiện của nữ diễn viên nhí Kim Gyuri trong drama \"My Ajusshi\" đã khiến cộng đồng mạng chú ý. Cô bé Kim Gyuri vào vai nữ chính Lee Jian (IU) lúc còn nhỏ. Nhà sản xuất phim đã xây dựng cho Kim Gyuri tạo hình giống hệt Jian khi lớn lên. Tuy nhiên đây không phải điều đáng chú ý nhất. \r\n\r\nádf\r\n\r\n\r\n\r\nKim Gyuri vào vai IU lúc nhỏ \r\n\r\nNetizen phát hiện ra, cô nhóc Kim Gyuri này giống IU một cách kỳ lạ. Từ đường nét gương mặt, đôi mắt, vầng trán, Gyuri đều có sự tương đồng với IU. So sánh ngoại hình của Kim Gyuri với IU lúc nhỏ, fan K-pop nhận ra cả hai giống hệt như \"đúc từ một khuôn\" mà ra. \r\n\r\n\r\n\r\nCả hai giống nhau một cách ngạc nhiên\r\n\r\n\r\n\r\nĐây là Kim Gyuri...\r\n\r\ndv\r\n\r\n...còn đây là IU khi còn bé\r\n\r\nThật ra từ trước khi tham gia bộ phim \"My Ajusshi\", Kim Gyuri đã là một người mẫu ảnh nhỏ tuổi nổi tiếng trên mạng xã hội. Cô nhóc được phát hiện nhờ ngoại hình xinh xắn, đáng yêu và đặc biệt là rất giống \"nữ hoàng nhạc số\". Cư dân mạng đặt biệt danh cho Kim Gyuri là \"bản sao của IU\" cũng là vì vậy. Có thể đây là lý do khiến đoàn làm phim để mắt tới Gyuri và lựa chọn cô nhóc cho vai Jian lúc nhỏ. \r\n\r\nNetizen Hàn bình luận bày tỏ sự ngạc nhiên:\"Em bé diễn viên nhí này thật quá đáng yêu\", \"Trời, họ không phải là chị em ruột sao?\", \"Gì cơ? Có có thể tìm ra một người giống y hệt IU để đóng vai IU lúc nhỏ thật ư? Ôi điên mất\". ', 'Thứ bảy, 21/04/2018 17:20', 'Hoàng Long', 5, 1),
(25, 'Netizen tranh cãi trước bài đăng nhận định rằng Seulgi (Red Velvet) đã tăng cân trong thời gian gần đây', '20180418163205-876c.jpg', 'Điều làm mọi người bối rối là dù gương mặt tròn hơn nhưng vóc dáng của nữ thần tượng vẫn quá mỏng.\r\nMới đây, một bài đăng trên cộng đồng trực tuyến cho rằng Seulgi (Red Velvet) đã tăng cân. Bằng chứng mà người này đưa ra là hình ảnh trong buổi biểu diễn ETUDE HOUSE Pink Play concert cách đây mấy ngày.\r\n\r\nSeulgi (Red Velvet) tăng cân\r\n\r\nNgay lập tức, một số netizen đồng ý rằng gò má của Seulgi trông đầy đặn hơn một chút so với trước đó:\r\n\r\nSeulgi (Red Velvet) tăng cân\r\n\r\n\"Yeap ... trông giống như má cô ấy tròn hơn một chút ...\"\r\n\r\n\"Mặc dù cô ấy đã tăng cân nhưng cô ấy vẫn có cơ bụng kìa ...? Tôi buồn thật sự.. ㅠㅠ\"\r\n\r\n\"Tôi nghĩ cô ấy xinh đẹp hơn nhưng đó là má của cô ấy sao ㅠ ㅠㅠㅠ Cô ấy thật tuyệt vời ㅠ ㅠㅠㅠ”\r\n\r\n\"Cô ấy chắc chắn đã tăng cân so với trước nhưng có một điều vẫn là sự thật, Seulgi ㅠㅠㅠ rất đẹp\"\r\n\r\nSeulgi (Red Velvet) tăng cân\r\n\r\nThế nhưng hầu hết cư dân mạng đều bối rối và tự hỏi rằng nếu cô ấy đã tăng cân thì tại sao vẫn trong khá mỏng như thế này:\r\n\r\nSeulgi (Red Velvet) tăng cân\r\n\r\n\"Wow, tiêu đề bài viết làm tôi sợ đấy nhưng cô ấy vẫn còn siêu mỏng kìa ... nữ thần của tôi 8 ㅅ 8\"\r\n\r\n\"Nó trông giống như bị sưng hơn là tăng cân... Seulgi vẫn là nữ thần trong lòng tôi ...\"\r\n\r\n\"Ở đâu! Cô ấy đã tăng cân ở đâu hả trời?\"\r\n\r\n\"Ủa đang tăng cân hả? Tôi không hiểu ㅜ ㅜㅜㅜㅜ\"', 'Thứ tư, 18/04/2018 17:00', 'Đức Trọng', 5, 0),
(26, 'Coi chừng nè: Lay (EXO) chính thức trở về Hàn Quốc, có lẽ nào EXO sắp comeback với OT9?', 'lay.jpg', 'Fan hâm mộ vô cùng mừng rỡ khi Lay đã có mặt tại sân bay Gimpo ngày hôm nay.\r\nSau tất cả những tin đồn rời nhóm, các EXO-L đã bắt gặp Lay trở về Hàn vào chiều nay. Nam thần tượng người Trung Quốc đã ra sân bay Bắc Kinh vào sáng nay. Anh chàng vừa xuất hiện tại sân bay Gimpo, Hàn Quốc cách đây ít phút. \r\n\r\n\r\n\r\n\r\n\r\nLay đột ngột trở về Hàn trong sự ngỡ ngàng của EXO-L\r\n\r\nNgay sau khi Lay đáp chuyến bay, quản lý của SM đã có mặt và đón Lay trở về. Tin tức này khiến người hâm mộ vô cùng phấn khích vì thành viên EXO đã quay lại Hàn sau một thời gian dài vắng bóng. Cụ thể, trong thời gian qua, Lay rất bận rộn với việc phát hành album solo, tham gia các dự án phim tại Trung Quốc. Gần đây anh chàng xuất hiện với vai trò là host của show thực tế sống còn \"Idol Producer\". Cũng chính vì vậy mà Lay không thể tham gia vào những đợt quảng bá và concert của nhóm. \r\n\r\n\r\n\r\nCó lẽ nào đội hình OT9 của EXO sắp quay trở lại? \r\n\r\nCác EXO-L đoán rằng sau khi kết thúc \"Idol Producer\", Lay đã trở về với cương vị là một thần tượng K-pop. Có thể lần này, Lay về Hàn là để cùng 8 thành viên còn lại bắt tay vào chuẩn bị album mới. Điều này nằm trong dự đoán của các fan hâm mộ. Ngay từ đầu năm, đã có tin đồn EXO sẽ comeback với OT9 và thành công còn khủng hơn năm ngoái. Cộng đồng fan rất kỳ vọng vào một bản hit có sức công phá mạnh như \"Growl\", giúp EXO củng cố vị trí boygroup hàng đầu K-pop. \r\n\r\nCùng chờ đợi sự trở lại của Lay và EXO nhé!', 'Thứ bảy, 21/04/2018 15:10', 'Hữu Luận', 5, 0),
(27, 'TWICE - \'quái vật\' nhiều mảng của Kpop', 'TWICEHeartShaker4.jpg', 'TWICE không chỉ nổi bật ở riêng mảng nhạc số như nhiều người vẫn nghĩ\r\nMặt trận youtube\r\n\r\nTWICE không hổ danh là nhóm nhạc nữ nổi tiếng nhất nhì Hàn Quốc hiện nay. Không chỉ đạt thành tích \"khủng\" về mảng nhạc số và bán album, nhóm nhạc nhà JYP luôn thu hút lượng xem lớn mỗi khi phát hành MV mới.\r\n\r\nLượt xem MV trên Youtube là một yếu tố khá chân thực phản ánh mức độ phổ biến của các nhóm nhạc Kpop. Vì thông qua đó, mọi người sẽ biết được sự quan tâm, chú ý của khán giả đến bài hát, nội dung video và đánh giá được mức độ thành công của sản phẩm âm nhạc như thế nào. \r\n\r\n\r\n\r\nPhát hành ngày 23/10/2016, MV \"TT\" của TWICE đã chính thức đạt 200 triệu lượt xem vào ngày 26/5. Với thành tích 215 ngày, TWICE là nhóm nhạc đạt 200 triệu lượt xem nhanh nhất Kpop. Đồng thời, chín cô gái cũng là nhóm nữ đầu tiên lập được thành tích này. Hiện nay MV đã có hơn 257 triệu lượt xem. \r\n\r\nVà vào ngày 9 tháng 8, MV \"Cheer Up\" đã xuất sắc cán mốc 200 triệu lượt xem trên Youtube. Trước đó, “Cheer Up” đạt cốc mốc 100 triệu view vào tháng 11 năm ngoái. Như vậy, TWICE hiện có tới 2 music video đạt 200 triệu lượt xem trên youtube, con số đáng mơ ước với mọi nhóm nhạc Kpop.\r\n\r\n\r\n\r\nHay mới đây nhất, vào ngày 30 tháng 8, MV \"Signal\" của TWICE đã chính thức cán mốc 100 triệu lượt xem trên YouTube. Phát hành vào ngày 15 tháng 5 năm 2017, \"Signal\" chỉ mất 107 ngày để vươn đến con số đáng mơ ước này.\r\n\r\nMặc dù, phải hứng chịu rất nhiều ý kiến trái chiều về chất lượng âm nhạc từ khi mới phát hành cho đến nay, thế nhưng, cả ca khúc lẫn MV \"Signal\" vẫn đủ sức mang về cho TWICE những thành công mà nhiều nghệ sĩ khác cũng phải ganh tị. Trên thực tế, \"Signal\" chính là MV thứ 5 của nhóm nữ nhà JYP vươn đến cột mốc 100 triệu lượt xem trên YouTube, theo sau \"Like OOH-AHH\", \"Cheer Up\", \"TT\" và \"Knock Knock\".\r\n\r\n\r\n\r\nĐiều đáng nói là, với việc \"Signal\" chính thức cán mốc 100 triệu view, tất cả các MV ca khúc chủ đề của TWICE (không tính MV tiếng Nhật) đều đã chạm tay đến con số ấn tượng này. Điều này cũng đồng nghĩa với việc TWICE đã trở thành nhóm nữ đầu tiên của Kpop (và cũng là duy nhất tính đến thời điểm hiện tại) có toàn bộ MV ca khúc chủ đề (tiếng Hàn) đều đạt đến cột mốc 100 triệu lượt xem trên YouTube.\r\n\r\nDoanh số album\r\n\r\nNếu như Kpop phân rạch ròi, nhóm nữ mạnh về digital, nhóm nam mạnh về album. Thế nhưng, cả 2 album mới nhất của TWICE đều khiến cho các nhóm nam trầm trồ. TWICE chỉ đứng sau một số nhóm nam đình đám như EXO, BTS, BIGBANG... Tuy nhiên, các cô nàng liên tục giành kỉ lục ở các nhóm nữ, thậm chí còn vượt qua số lượng album của một số nhóm nam khác. \r\n\r\n \r\n\r\n\"Twice coaster: Lane 2\" là album đặc biệt đầu tiên của TWICE. Album được phát hành trực tuyến trên các trang âm nhạc và các cửa hàng vào ngày 20 tháng 2 năm 2017. Chỉ sau vài phút, trang web bán hàng trực tuyến Synnara Record bị dừng hoạt động, rơi vào trạng thái treo máy vì lượng đặt hàng quá lớn. Tỉnh đến khoảng nửa năm 2017, album đã bán được 273.372 bản. Một con số cực kỳ ấn tượng đối với 1 nhóm nhạc nữ.\r\n\r\n \r\n\r\nVào ngày 15 tháng 5 năm 2017 Các cô gái nhà JYP đã trở lại với mini album thứ 4 “Signal”. Bài hát mới của họ nhanh chóng leo lên các bảng xếp hạng âm nhạc. Thành công của TWICE với \"Signal\" không dừng lại ở đó. Họ cũng đã phá vỡ kỉ lục của chính họ cho album bán chạy nhất cho nhóm nhạc nữ trong ngày đầu tiên trên Hanteo.\r\n\r\nTrước đó, TWICE đã tạo ra kỉ lục với việc bán 34,255 bản cho \"TWICEcoaster: LANE 1” cho ngày đầu tiên. Với “Signal” TWICE đã bán được 42,444 bản và tạo ra một kỉ lục mới.\r\n\r\nKhông chỉ vậy, \"Signal\" đã đứng vị trí #11 ở lần đầu xuất hiện trên Heatseekers Albums. Tính đến khoảng nửa năm 2017, album lần này của TWICE bán được 248.550 bản.\r\n\r\nNgày 28 tháng 6 năm 2017, TWICE ra mắt tại Nhật Bản với album mang tên \"#TWICE\" . Album bao gồm các ca khúc như \"Like OOH-AHH\", \"Cheer Up\", \"TT\", \"Knock Knock\", \"Signal\", với cả hai phiên bản tiếng Hàn và tiếng Nhật và đã đứng hạng 2 trên Oricon Daily Chart với ghi nhận 46,871 bản bán được chỉ trong ngày đầu tiên.\r\n\r\n\r\n\r\nVào ngày 10 tháng 8, Hiệp hội ngành công nghiệp thu âm Nhật Bản (RIAJ) đã tiết lộ rằng album tiếng Nhật đầu tiên của TWICE đã bán được 250.000 bản và đạt được chứng chỉ bạch kim.\r\n\r\nChương trình âm nhạc cuối tuần \r\n\r\n Các ca khúc của TWICE khi comeback đều giành cho mình hàng loạt cúp kéo dài trong nhiều tuần. Kể đến là \"Cheer Up\" được 11 cúp, \"TT\" 13 cúp.\r\n\r\nBài hát mới nhất \"Signal\", bất chấp những ý kiến trái chiều từ công chúng, \"Signal\" vẫn hiên ngang đứng vững trên các bảng xếp hạng nhạc số và liên tục được đề cử tranh cúp các chương trình âm nhạc cuối tuần. Và cho dù có quảng bá trên sân khấu hay không thì TWICE vẫn ôm cúp về nhà đều đặn.\r\n\r\n\r\nNgày 11/6, ca khúc này đã mang về cho TWICE chiếc cúp thứ 11 đồng thời là Triple Crown (3 tuần chiến thắng liên tiếp) trên sân khấu Inkigayo của đài SBS trước cả PSY lẫn SISTAR.\r\n\r\nDoanh thu concert\r\n\r\n \r\n\r\nNgày 10 tháng 1, JYP Entertainment đã thông báo đến người hâm mộ về tour diễn đầu tiên trong sự nghiệp của TWICE thông qua tài khoản SNS chính thức của nhóm cũng như poster và thời gian diễn ra tour diễn. Theo đó, tour diễn của TWICE mang tên: “TWICELAND -The Opening” sẽ tổ chức trong 3 ngày từ 17/2 đến 19/2 tại sân vận động có sức chứa 5000 người (chưa bao gồm khu vực đứng) SK Olympic Handball Gymnasium. Như vậy, TWICE lại tiếp tục thiết lập 1 kỉ lục mới khi là nhóm nữ tổ chức concert riêng sớm nhất, chỉ 1 năm 4 tháng sau khi ra mắt. Bên cạnh đó, JYP cũng đã tiết lộ TWICE sẽ tiếp tục chuẩn bị cho World Tour sau concert.\r\n\r\n\r\nChỉ hơn một tháng sau, ngày 20 tháng 2, TWICE bắt đầu mở đầu bán vé cho tour diễn \"TWICELAND -The Opening\" và chỉ hơn 40 phút sau khi hệ thống bán vé được mở, lượng vé ở cả ba ngày biểu diễn của TWICE đều đã được bán sạch. Ngày 25 tháng 1 và ngày 26 tháng 1, JYP tiếp tục thông báo rằng nhóm sẽ tổ chức tour diễn tại Bangkok, Thái Lan vào ngày 8 tháng 4 và tại Singapore vào ngày 29 tháng 4.\r\n\r\n\r\nMới đây, ngày 2 tháng 7 năm 2017, TWICE tổ chức showcase đầu tiên tại Nhật - “TWICE DEBUT SHOWCASE Touchdown in JAPAN” tại Tokyo Metropolitan Gym. Theo kế hoạch ban đầu, girlgroup này sẽ tổ chức 1 buổi duy nhất để ra mắt thế nhưng, vì số lượng fan đăng ký tham gia quá lớn, phía JYP đã bổ sung thêm 2 buổi, thu hút 15.000 người hâm mộ tại đất nước Mặt trời mọc có mặt trong chương trình. ', 'Thứ sáu, 08/09/2017 10:52', 'Thảo Trang', 5, 0),
(30, 'Ronaldo lại khiến Rashford \'phát điên\' vì sung sướng', '4B82BD68000005780imagea28_1524594553155.jpg', 'Sau món quà đặc biệt cách đây hai tuần, Cristiano Ronaldo một lần nữa mang đến niềm vui khó tả cho truyền nhân của mình tại sân Old Trafford.\r\nMarcus Rashford được các CĐV gọi là chàng trai may mắn nhất thế giới sau khi nhận được món quà siêu đặc biệt từ Ronaldo. Theo đó, siêu sao người Bồ Đào Nha trực tiếp đóng gói và gửi đến cho Rashford đôi giày \"khủng\" mang thương hiệu “CR7 Red”.\r\n\r\nNgay lập tức, Rashford sung sướng khoe trên trang cá nhân với dòng trạng thái: “Cảm ơn anh vì món quà, Cristiano Ronaldo”.\r\n\r\nRonaldo lai khien Rashford \'phat dien\' vi sung suong hinh anh 1\r\nRashford hạnh phúc với món quà \"CR7 Red\" từ thần tượng. Ảnh: Instagram.\r\nTrong hôm nay, Ronaldo tiếp tục mang đến niềm vui cho người được xem là truyền nhân của mình ở MU. Lần này, Ronaldo tặng Rashford chiếc áo đấu của anh, kèm theo đó là chữ ký và lời nhắn: “Marcus, hãy tiếp tục giữ vững công việc rất tốt của mình”\r\n\r\nRạng sáng 26/4, Ronaldo sẽ cùng Real Madrid bước vào trận đấu cực kỳ quan trọng với Bayern Munich ở bán kết Champions League 2017/18. Ronaldo đang rất khao khát có được danh hiệu vô địch lần thứ ba liên tiếp ở đấu trường châu Âu, do đó trước hết cần phải đánh bại được Bayern Munich.\r\n\r\nRonaldo lai khien Rashford \'phat dien\' vi sung suong hinh anh 2Phóng to\r\nChiếc áo đấu kèm chữ ký và lời nhắn nhủ của Ronaldo cho Rashford. Ảnh: Daily Mail.\r\nVới Rashford, tài năng trẻ của MU vẫn đang miệt mài tập luyện để tìm kiếm cơ hội ra sân ở đội hình xuất phát. Hiện tại, Rashford chỉ là sự lựa chọn thứ yếu của Jose Mourinho bên hành lang cánh, xếp sau Anthony Martial và Alexis Sanchez. 3 tháng đã qua, số lần Rashford được góp mặt trong đội hình chính là rất hạn chế.', '16:42 25/04/2018', 'Nhat Hoa', 2, 1),
(32, 'Mai Phương Thúy mặc xẻ đến rốn, phô bày dáng vóc \"phồn thực\"', 'maiphuongthuy.jpg', 'Sau 11 năm đăng quang, Mai Phương Thúy vẫn được coi là một trong những Hoa hậu Việt Nam được khán giả quan tâm nhất. Cô ngày càng hoàn thiện về phong cách và thần thái. Với lợi thế ngoại hình gợi cảm, chiều cao nổi trội, người đẹp luôn xuất hiện nổi bật ở mọi nơi.\r\n\r\nKhi đi dự sự kiện, Mai Phương Thúy trung thành với phong cách gợi cảm, khoe khéo thân hình chuẩn. Váy cắt khoét sâu hay xẻ cao cũng không làm khó được cô. Người đẹp còn thường dùng thêm trang sức hàng hiệu để tăng thêm phần quyến rũ cho các trang phục đơn sắc.', 'Thứ Hai, ngày 05/02/2018 09:47 AM (GMT+7)', 'Anh Đào', 4, 0),
(28, 'Người dân Sài Gòn dâng lễ vua Hùng ngày giỗ Tổ', 'dang_huong_2_zing.JPG', 'Hàng nghìn người dân TP.HCM và các tỉnh lân cận mang theo bánh chưng, hương hoa xếp hàng dài tại Khu tưởng niệm các vua Hùng (quận 9), sáng 25/4.\r\nHàng nghìn người dâng hương các vua Hùng ở Sài Gòn Hàng nghìn người dân TP và các tỉnh lân cận mang theo bánh chưng, hương hoa dâng các vua Hùng trong ngày giỗ Tổ tại khu tưởng niệm ở quận 9, TP.HCM.\r\nNguoi dan Sai Gon dang le vua Hung ngay gio To hinh anh 1\r\nSáng 25/4, hàng nghìn người dân TP.HCM và các tỉnh lân cận đổ về Khu tưởng niệm các vua Hùng để thắp hương, tưởng niệm trong ngày giỗ Tổ.\r\nNguoi dan Sai Gon dang le vua Hung ngay gio To hinh anh 2\r\nĐồ lễ cúng gồm kiệu trái cây, kiệu bánh chưng bánh giầy được rước từ dưới chân Khu tưởng niệm lên đặt trang trọng tại ban thờ chính của đền, là nơi thờ Quốc Tổ Hùng Vương.\r\nNguoi dan Sai Gon dang le vua Hung ngay gio To hinh anh 3\r\nDự lễ có Nguyên Thủ tướng Nguyễn Tấn Dũng, Bí thư Thành ủy TP.HCM Nguyễn Thiện Nhân, Chủ tịch UBND TP Nguyễn Thành Phong...\r\nNguoi dan Sai Gon dang le vua Hung ngay gio To hinh anh 4\r\nSau nghi thức lễ cúng Quốc Tổ Hùng Vương, hàng nghìn người dân TP.HCM và các tỉnh lân cận tập trung tại khu vực tiểu đình giữa đền chờ vào dâng lễ, thắp hương.\r\nNguoi dan Sai Gon dang le vua Hung ngay gio To hinh anh 5\r\nTừng đoàn người xếp thành những hàng dài vào trong.\r\nNguoi dan Sai Gon dang le vua Hung ngay gio To hinh anh 6\r\nBà Nguyễn Thị Tú (Biên Hòa, Đồng Nai) cho biết bà cùng hàng xóm lên Khu tưởng niệm từ sáng sớm để chờ lên đền: \"Chưa có dịp ra đất tổ Phú Thọ nên hai năm nay, cứ vào dịp giỗ Tổ chúng tôi lại tới để thắp hương, tưởng nhớ các vua Hùng tại đây\".\r\nNguoi dan Sai Gon dang le vua Hung ngay gio To hinh anh 7\r\nMỗi người một nén hương tỏ lòng thành kính.\r\nNguoi dan Sai Gon dang le vua Hung ngay gio To hinh anh 8\r\nThời tiết nóng bức, nhưng nhiều người vẫn chờ đợi khá lâu đến lượt để vào thắp hương.\r\nNguoi dan Sai Gon dang le vua Hung ngay gio To hinh anh 9\r\n\"Đây là lần đầu tiên hai mẹ con mang bánh chưng, bánh giầy tới khu tưởng niệm để dâng lên các vua Hùng. Gia đình chúng tôi rất trân trọng ngày giỗ Tổ, thường xuyên giáo dục con cháu luôn phải tưởng nhớ đến cội nguồn của mình\", bà Hoàn Thị Hoa nói.\r\nNguoi dan Sai Gon dang le vua Hung ngay gio To hinh anh 10\r\nChị Nguyễn Thị Lụa (quận 2) đội nguyên mâm lễ vào dâng lên bàn thờ Quốc Tổ Hùng Vương.\r\nNguoi dan Sai Gon dang le vua Hung ngay gio To hinh anh 11\r\nGiữa trưa, rất nhiều người dân và du khách vẫn tập trung về khu đền.\r\nNguoi dan Sai Gon dang le vua Hung ngay gio To hinh anh 12\r\nKhu tưởng niệm các vua Hùng hôm nay còn có nhiều hoạt động như thi cắm hoa, trang trí trái cây, thả diều, gói bánh chưng...\r\nNguoi dan Sai Gon dang le vua Hung ngay gio To hinh anh 13Phóng to\r\nBên cạnh đó các hoạt động vui chơi, nghệ thuật truyền thống cũng được tổ chức thu hút đông đảo người dân, du khách thưởng thức.', '14:33 25/04/2018', 'zing.vn', 1, 1),
(55, 'Những kẻ không sợ chết leo lên nóc nhà chọc trời làm gì?', '28754278_185059.jpg', 'Thời gian qua, với sự bùng nổ của mạng xã hội, các trào lưu kỳ lạ, mạo hiểm xuất hiện ngày càng nhiều, thu hút sự quan tâm của giới trẻ, trong đó không thể không kể đến \"rooftopping\".\r\n\r\n\"Rooftopping\" tạm dịch là xu hướng chụp ảnh nóc nhà. Nó được coi như \"cơn ác mộng chết chóc\" khi người tham gia, hầu hết đều còn trẻ, không sử dụng đồ bảo hộ thực hiện các động tác nguy hiểm, hay đu mình lơ lửng bên ngoài những tòa nhà chọc trời. Mục đích của họ là có ảnh chụp, đoạn phim ngoạn mục, đổi lấy sự tung hô trên thế giới ảo, theo The Sun.\r\n\r\nVới mức độ táo bạo ngày càng tăng, những người trẻ yêu thích trào lưu này không chỉ dừng lại ở việc chụp ảnh, mà còn quay video hay thể hiện màn parkour - trò mạo hiểm mang phong cách hip hop với những cú nhảy, nhào lộn - trên nóc nhà.', '18/05/2018', 'Ánh Hoàng', 3, 1),
(56, 'Tuyệt tác Aston Martin Vantage 2019 đến Đông Nam Á', '01_vantage2019_zing_2.jpg', 'Một trong những mẫu xe có thiết kế đẹp nhất trong vài năm trở lại đây xuất hiện tại Thái Lan và dự kiến sớm về Việt Nam.\r\nAston Martin vốn không phải là một thương hiệu quá quen thuộc với phần đông người Việt. Tuy nhiên, những mẫu xe cao cấp đến từ Anh Quốc này dự kiến được phân phối chính hãng bởi đơn vị đang bán Bentley và Lamborghini ngay năm nay. Trong số đó có chiếc Vantage thế hệ mới, đang trưng bày tại triển lãm Bangkok Motor Show 2018.\r\n\r\nTuyet tac Aston Martin Vantage 2019 den Dong Nam A hinh anh 1\r\nSản phẩm mới nhất của Aston Martin là một trong những mẫu xe thu hút nhất tại triển lãm Bangkok Motor Show 2018. \r\nThái Lan cũng là quốc gia Đông Nam Á đầu tiên có vinh dự đón nhận một chiếc Aston Martin Vantage 2019 \"bằng xương bằng thịt\", chỉ 5 tháng sau khi mẫu xe này được giới thiệu ở tầm quốc tế. So với thế hệ thứ nhất, Vantage 2019 được thiết kế lại hoàn toàn theo triết lý mới: thể thao, năng động và linh hoạt, nhưng phải hiệu quả và thật hấp dẫn.', '15:13 30/03/2018', 'Nguyễn Nam', 1, 0);

